mapper.select:
select i.id, i.main_order_no mainOrderNo, i.order_no orderNo,i.status, i.process_status processStatus, i.create_user createUser, i.create_time createTime, i.unit_code subUnitCode, i.legal_entity_id, i.legal_name subLegalName, ic.supplier_id supplierId, ic.vehicle_size vehicleSize, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.order_no = i.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.order_no = i.`order_no`)>0 THEN TRUE ELSE FALSE END) cost FROM order_inland_transport i left join order_inland_send_cars ic on i.order_no=ic.order_no
and i.order_no like concat('%',#{form.orderNo},'%')
and i.main_order_no IN
#{item}
and i.status = #{form.status}
and i.process_status IN
#{item}
and date_format(i.create_time,'%Y-%m-%d') >= date_format(#{form.beginCreatedTime},'%Y-%m-%d')
and date_format(i.create_time,'%Y-%m-%d') <= date_format(#{form.endCreatedTime},'%Y-%m-%d')
and i.vehicle_size = #{form.vehicleSize}
AND (EXISTS (SELECT 1 FROM order_payment_cost opc WHERE opc.`order_no` = i.order_no AND opc.`status` = '2' and opc.sub_type = 'nl') OR EXISTS (SELECT 1 FROM order_receivable_cost orc WHERE orc.`order_no` = i.order_no AND orc.`status` = '2' and orc.sub_type = 'nl'))
and i.`legal_entity_id` in
#{legalId}
order by i.id desc
