mapper.select:
SELECT id 'id', cust_id 'custId', manage_user_id 'manageUserId', manage_username 'manageUsername', manage_roles 'manageRoles', manager_roles_name 'managerRolesName', generate_date 'generateDate', manager_business_code 'managerBusinessCode', manager_business_name 'managerBusinessName', is_charger 'isCharger', is_sale 'isSale', tenant_code 'tenantCode', remark 'remark', is_deleted 'isDeleted', create_by 'createBy', create_time 'createTime', update_by 'updateBy', update_time 'updateTime' FROM crm_customer_manager WHERE is_deleted = 0
AND cust_id = #{custId}
AND manage_user_id = #{manageUserId}
AND manage_username LIKE CONCAT('%',#{manageUsername}, '%')
AND manage_roles LIKE CONCAT('%',#{manageRoles}, '%')
AND manager_roles_name LIKE CONCAT('%',#{managerRolesName}, '%')
AND generate_date = #{generateDate}
AND manager_business_code LIKE CONCAT('%',#{managerBusinessCode}, '%')
AND manager_business_name LIKE CONCAT('%',#{managerBusinessName}, '%')
AND is_charger = #{isCharger}
AND is_sale = #{isSale}
AND tenant_code LIKE CONCAT('%',#{tenantCode}, '%')
AND remark LIKE CONCAT('%',#{remark}, '%')
