mapper.select:
select bill_no billNo, currency_code currencyCode,sum(money) amount from order_bill_cost_total where bill_no in
#{billNo}
group by bill_no,currency_code -- (select SUM(o.money) from order_bill_cost_total o where o.bill_no = orbd.bill_no and o.currency_code='CNY') -- rmb, -- (select SUM(o.money) from order_bill_cost_total o where o.bill_no = orbd.bill_no and o.currency_code='USD') -- dollar, -- (select SUM(o.money) from order_bill_cost_total o where o.bill_no = orbd.bill_no and o.currency_code='EUR') -- euro, -- (select SUM(o.money) from order_bill_cost_total o where o.bill_no = orbd.bill_no and o.currency_code='HKD') -- hKDollar,

select sum(money)
from order_bill_cost_total obct


        left join order_payment_cost oc on obct.cost_id=oc.id


        left join order_receivable_cost oc on obct.cost_id=oc.id





            and oc.main_order_no=#{orderNo}


            and oc.order_no=#{orderNo}


    and obct.money_type=#{moneyType}
    and obct.bill_no=#{billNo}
