mapper.select:
select temp.orderNo,temp.createTimeStr,temp.legalName,temp.customerCode,temp.customerName,temp.ywName,temp.recBillNo,temp.recAccountTermStr,temp.recRmb,temp.recDollar,temp.recEuro,temp.recHkDollar,temp.recLocalAmount,temp.recStatus, (case (select sum(cav.local_money) from cancel_after_verification cav where cav.bill_no = temp.recBillNo) when temp.recLocalAmount THEN '已核销' else '未核销' END) recCostStatus, temp.payBillNo,temp.payAccountTermStr,temp.payRmb,temp.payDollar,temp.payEuro,temp.payHkDollar,temp.payLocalAmount,temp.payStatus, (case (select sum(cav.local_money) from cancel_after_verification cav where cav.bill_no = temp.payBillNo) when temp.recLocalAmount THEN '已核销' else '未核销' END) payCostStatus, (temp.recLocalAmount - temp.payLocalAmount) profit from ( SELECT oi.order_no orderNo, DATE_FORMAT( oi.create_time, '%Y-%m-%d' ) createTimeStr, oi.legal_name legalName, oi.customer_code customerCode, oi.customer_name customerName, oi.biz_uname ywName, (select group_concat(distinct orbd.bill_no) from order_receivable_bill_detail orbd where orbd.order_no = oi.order_no group by orbd.order_no) recBillNo, (select group_concat(distinct orbd.account_term) from order_receivable_bill_detail orbd where orbd.order_no = oi.order_no group by orbd.order_no) recAccountTermStr, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no in (select t.bill_no from order_receivable_bill_detail t where t.order_no = oi.order_no) and obct.currency_code = 'CNY' and obct.money_type='2') recRmb, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no in (select t.bill_no from order_receivable_bill_detail t where t.order_no = oi.order_no) and obct.currency_code = 'USD' and obct.money_type='2') recDollar, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no in (select t.bill_no from order_receivable_bill_detail t where t.order_no = oi.order_no) and obct.currency_code = 'EUR' and obct.money_type='2') recEuro, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no in (select t.bill_no from order_receivable_bill_detail t where t.order_no = oi.order_no) and obct.currency_code = 'HKD' and obct.money_type='2') recHkDollar, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no in (select t.bill_no from order_receivable_bill_detail t where t.order_no = oi.order_no) and obct.currency_code = 'CNY' and obct.money_type='2') recLocalAmount, (select group_concat(distinct orbd.audit_status) from order_receivable_bill_detail orbd where orbd.order_no = oi.order_no group by orbd.order_no) recStatus, (select group_concat(distinct opbd.bill_no) from order_payment_bill_detail opbd where opbd.order_no = oi.order_no group by opbd.order_no) payBillNo, (select group_concat(distinct opbd.account_term) from order_payment_bill_detail opbd where opbd.order_no = oi.order_no group by opbd.order_no) payAccountTermStr, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no in (select t.bill_no from order_payment_bill_detail t where t.order_no = oi.order_no) and obct.currency_code = 'CNY' and obct.money_type='1') payRmb, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no in (select t.bill_no from order_payment_bill_detail t where t.order_no = oi.order_no) and obct.currency_code = 'USD' and obct.money_type='1') payDollar, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no in (select t.bill_no from order_payment_bill_detail t where t.order_no = oi.order_no) and obct.currency_code = 'EUR' and obct.money_type='1') payEuro, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no in (select t.bill_no from order_payment_bill_detail t where t.order_no = oi.order_no) and obct.currency_code = 'HKD' and obct.money_type='1') payHkDollar, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no in (select t.bill_no from order_payment_bill_detail t where t.order_no = oi.order_no) and obct.currency_code = 'CNY' and obct.money_type='1') payLocalAmount, (select group_concat(distinct opbd.audit_status) from order_payment_bill_detail opbd where opbd.order_no = oi.order_no group by opbd.order_no) payStatus FROM order_info oi where EXISTS (select opc.main_order_no from order_payment_cost opc where opc.main_order_no = oi.order_no and opc.`status` = '3') or EXISTS (select orc.main_order_no from order_receivable_cost orc where orc.main_order_no = oi.order_no and orc.`status` = '3') ) temp where 1 = 1
and temp.orderNo = #{form.orderNo}
and temp.customerName like concat('%',#{form.customerName},'%')
and temp.legalName like concat('%',#{form.legalName},'%')
and temp.ywName like concat('%',#{form.ywName},'%')
and temp.recStatus = #{form.recStatus}
and (case (select sum(cav.local_money) from cancel_after_verification cav where cav.bill_no = temp.recBillNo) when temp.recLocalAmount THEN '已核销' else '未核销' END) = #{form.recCostStatus}
and (case (select sum(cav.local_money) from cancel_after_verification cav where cav.bill_no = temp.payBillNo) when temp.recLocalAmount THEN '已核销' else '未核销' END) = #{form.payCostStatus}
and temp.payStatus = #{form.payStatus}
and temp.createTimeStr = #{form.createTimeStr}
and temp.recAccountTermStr = #{form.recAccountTermStr}
and temp.payAccountTermStr = #{form.payAccountTermStr}

select * from ( SELECT opbd.id billDetailId, oi.order_no orderNo, opc.order_no subOrderNo, oi.biz_code bizCode, (select pb.`name` from product_biz pb where pb.id_code = oi.biz_code) bizCodeDesc, oi.create_time createdTimeStr, (select si.supplier_ch_name from supplier_info si where si.supplier_code = opc.customer_code) supplierChName, (SELECT GROUP_CONCAT(ota.goods_desc) FROM order_take_adr ota left join order_transport ot on ot.order_no = ota.order_no WHERE ot.main_order_no = oi.order_no and ota.opr_type = '1' group by ot.order_no) goodsDesc, (SELECT GROUP_CONCAT(concat(ifnull(da.city_name,''), da.address )) FROM delivery_address da LEFT JOIN order_take_adr ota ON da.id = ota.delivery_id left join order_transport ot on ot.order_no = ota.order_no WHERE ot.main_order_no = oi.order_no and ota.opr_type = '1' group by ot.order_no) startAddress, (SELECT GROUP_CONCAT(concat(ifnull(da.city_name,''), da.address )) FROM delivery_address da LEFT JOIN order_take_adr ota ON da.id = ota.delivery_id left join order_transport ot on ot.order_no = ota.order_no where ot.main_order_no = oi.order_no and ota.opr_type = '2' group by ot.order_no) endAddress, (select concat( vi.plate_number,' ',vi.hk_number) from order_send_cars osc left join order_transport ot on osc.order_no = ot.order_no left join vehicle_info vi on vi.id = osc.vehicle_id where ot.main_order_no = oi.order_no) licensePlate, (select GROUP_CONCAT(oc.yun_customs_no) from order_customs oc where oc.main_order_no = oi.order_no GROUP BY oc.main_order_no) yunCustomsNo, (select cg.`name` from cost_genre cg where cg.id = opc.cost_genre_id) costGenreName, opc.cost_genre_id, (select pb.cost_genre_ids from product_biz pb where pb.id_code = oi.biz_code) costGenreStr, opc.id costId, (select ct.code_name from cost_type ct where ct.id = opc.cost_type_id) costTypeName, (select ci.`name` from cost_info ci where ci.id_code = opc.cost_code) costName, (select SUM(obct.money) from order_bill_cost_total obct where obct.cost_id = opbd.cost_id and obct.money_type='1' and obct.currency_code = 'CNY') rmb, (select SUM(obct.money) from order_bill_cost_total obct where obct.cost_id = opbd.cost_id and obct.money_type='1' and obct.currency_code = 'USD') dollar, (select SUM(obct.money) from order_bill_cost_total obct where obct.cost_id = opbd.cost_id and obct.money_type='1' and obct.currency_code = 'EUR') euro, (select SUM(obct.money) from order_bill_cost_total obct where obct.cost_id = opbd.cost_id and obct.money_type='1' and obct.currency_code = 'HKD') hKDollar, ifnull(opbd.tax_rate,(select cg.tax_rate from cost_genre cg where cg.id = opc.cost_genre_id)) taxRate, opbd.remarks, opbd.created_time createTimeStr, opbd.bill_no, (select ci.currency_name from currency_info ci where ci.currency_code = opbd.settlement_currency) settlementCurrency, orbct.money settlementAmount,orbct.exchange_rate exchangeRate FROM order_payment_bill_detail opbd left join order_payment_bill opb on opbd.bill_id = opb.id left join order_info oi on oi.order_no = opbd.order_no left join order_payment_cost opc on opc.id = opbd.cost_id left join order_bill_cost_total orbct on orbct.bill_no = opbd.bill_no and orbct.cost_id = opbd.cost_id and money_type='1' where opbd.bill_no = #{form.billNo} ) temp
and temp.orderNo = #{form.orderNo}
and temp.bizCode = #{form.bizCode}
and temp.startAddress like concat('%',#{form.startAddress},'%')
and temp.endAddress like concat('%',#{form.endAddress},'%')
and temp.costTypeName like concat('%',#{form.costTypeName},'%')
and temp.costName like concat('%',#{form.costName},'%')
and temp.costGenreId = #{form.costGenreId}
and temp.createdTimeStr <= #{form.endCreateTimeStr}
and temp.createdTimeStr <= #{form.beginCreateTimeStr}
