mapper.select:
SELECT opbd.bill_no billNo, opb.legal_name legalName, opb.customer_name customerName, DATE_FORMAT(opbd.begin_account_term,'%Y-%m-%d') beginAccountTermStr, DATE_FORMAT(opbd.end_account_term,'%Y-%m-%d') endAccountTermStr, (select SUM(o.money) from order_bill_cost_total o where o.bill_no = opbd.bill_no and o.currency_code='RMB') rmb, (select SUM(o.money) from order_bill_cost_total o where o.bill_no = opbd.bill_no and o.currency_code='MY') dollar, (select SUM(o.money) from order_bill_cost_total o where o.bill_no = opbd.bill_no and o.currency_code='OY') euro, (select SUM(o.money) from order_bill_cost_total o where o.bill_no = opbd.bill_no and o.currency_code='GB') hKDollar, (select sum(m.money) from make_invoice m where m.opr_type = '2' and m.bill_no = opbd.bill_no and m.`status` = '1') heXiaoAmount, (opbd.payment_amount - (select sum(m.money) from make_invoice m where m.opr_type = '2' and m.bill_no = opbd.bill_no and m.`status` = '1')) notHeXiaoAmount,opbd.settlement_currency settlementCurrency,opbd.audit_status auditStatus,opbd.apply_status applyStatus, opbd.make_user makeUser, opbd.make_time makeTimeStr, opbd.audit_user auditUser, opbd.audit_time auditTimeStr, (select c.created_user from cancel_after_verification c where c.bill_no = opbd.bill_no order by c.created_time desc limit 1) heXiaoUser, (select c.real_receive_time from cancel_after_verification c where c.bill_no = opbd.bill_no order by c.created_time desc limit 1) heXiaoTimeStr FROM order_payment_bill_detail opbd LEFT JOIN order_payment_bill opb ON opbd.bill_id = opb.id where 1 = 1
and opb.customer_name like concat('%',#{form.customerName},'%')
and opb.legal_name like concat('%',#{form.legalName},'%')
and opbd.bill_no like concat('%',#{form.billNo},'%')
and opbd.end_account_term <= #{form.endAccountTerm}
and opbd.begin_account_term >= #{form.beginAccountTerm}
and opbd.settlement_currency >= #{form.currencyCode}
and opbd.audit_status >= #{form.auditStatus}
and opbd.apply_status >= #{form.applyStatus}

select temp.orderNo,temp.createTimeStr,temp.legalName,temp.customerCode,temp.customerName,temp.ywName,temp.sBillNo,temp.sBeginAccountTermStr,temp.sEndAccountTermStr,temp.sRmb,temp.sDollar,temp.sEuro,temp.sHkDollar,temp.sLocalAmount,temp.sStatus, (case (select sum(cav.local_money) from cancel_after_verification cav where cav.bill_no = temp.sBillNo) when temp.sLocalAmount THEN '已核销' else '未核销' END) sCostStatus, temp.fBillNo,temp.fBeginAccountTermStr,temp.fEndAccountTermStr,temp.fRmb,temp.fDollar,temp.fEuro,temp.fHkDollar,temp.fLocalAmount,temp.fStatus,(case (select sum(cav.local_money) from cancel_after_verification cav where cav.bill_no = temp.fBillNo) when temp.sLocalAmount THEN '已核销' else '未核销' END) fCostStatus, (temp.sLocalAmount - temp.fLocalAmount) profit from ( SELECT oi.order_no orderNo, oi.create_time createTimeStr, oi.legal_name legalName, oi.customer_code customerCode, oi.customer_name customerName, oi.biz_uname ywName, orbd.bill_no sBillNo, orbd.begin_account_term sBeginAccountTermStr, orbd.end_account_term sEndAccountTermStr, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no = orbd.bill_no and obct.currency_code = 'RMB' and obct.money_type='2') sRmb, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no = orbd.bill_no and obct.currency_code = 'MY' and obct.money_type='2') sDollar, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no = orbd.bill_no and obct.currency_code = 'OY' and obct.money_type='2') sEuro, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no = orbd.bill_no and obct.currency_code = 'GB' and obct.money_type='2') sHkDollar, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no = orbd.bill_no and obct.currency_code = 'GB' and obct.money_type='2') sLocalAmount, orbd.audit_status sStatus, opbd.bill_no fBillNo, opbd.begin_account_term fBeginAccountTermStr, opbd.end_account_term fEndAccountTermStr, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no = opbd.bill_no and obct.currency_code = 'RMB' and obct.money_type='1') fRmb, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no = opbd.bill_no and obct.currency_code = 'MY' and obct.money_type='1') fDollar, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no = opbd.bill_no and obct.currency_code = 'OY' and obct.money_type='1') fEuro, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no = opbd.bill_no and obct.currency_code = 'GB' and obct.money_type='1') fHkDollar, (select SUM(obct.money) from order_bill_cost_total obct where obct.bill_no = opbd.bill_no and obct.currency_code = 'GB' and obct.money_type='1') fLocalAmount, opbd.audit_status fStatus FROM order_info oi left join order_payment_bill_detail opbd on oi.order_no = opbd.order_no left join order_receivable_bill_detail orbd on oi.order_no = opbd.order_no) temp where 1 = 1
and temp.orderNo = #{form.orderNo}
and temp.customerName like concat('%',#{form.customerName},'%')
and temp.legalName like concat('%',#{form.legalName},'%')
and temp.ywName like concat('%',#{form.ywName},'%')
and temp.sStatus = #{form.sStatus}
and temp.sCostStatus = #{form.sCostStatus}
and temp.fCostStatus = #{form.fCostStatus}
and temp.fStatus = #{form.fStatus}
and temp.beginAccountTermStr >= #{form.beginAccountTermStr}
and temp.endAccountTermStr <= #{form.endAccountTermStr}
and temp.createTimeStr = #{form.createTimeStr}

select * from ( SELECT oi.order_no orderNo, oi.biz_code bizCode, (select pb.`name` from product_biz pb where pb.id_code = oi.biz_code) bizCodeDesc, oi.create_time createdTimeStr, oi.customer_name customerName, (SELECT concat( da.city_name, da.address ) FROM delivery_address da LEFT JOIN order_take_adr ota ON da.id = ota.delivery_id left join order_transport ot on ot.order_no = ota.order_no WHERE ot.main_order_no = oi.order_no and ota.opr_type = '1' LIMIT 1) startAddress, (SELECT concat( da.city_name, da.address ) FROM delivery_address da LEFT JOIN order_take_adr ota ON da.id = ota.delivery_id costGenreId, left join order_transport ot on ot.order_no = ota.order_no where ot.main_order_no = oi.order_no and ota.opr_type = '2' LIMIT 1) endAddress, (select concat( osc.license_plate, osc.hk_license_plate ) from order_send_cars osc left join order_transport ot on osc.order_no = ot.order_no where ot.main_order_no = oi.order_no) licensePlate, (select oc.yun_customs_no from order_customs oc where oc.main_order_no = oi.order_no) yunCustomsNo, (select cg.`name` from cost_genre cg where cg.id = opc.cost_genre_id) costGenreName, opc.cost_genre_id (select ct.code_name from cost_type ct where ct.id = opc.cost_type_id) costTypeName, (select ci.`name` from cost_info ci where ci.id_code = opc.cost_code) costName, (select SUM(obct.money) from order_bill_cost_total obct where obct.cost_id = opbd.cost_id and obct.money_type='1' and obct.currency_code = 'RMB') rmb, (select SUM(obct.money) from order_bill_cost_total obct where obct.cost_id = opbd.cost_id and obct.money_type='1' and obct.currency_code = 'MY') dollar, (select SUM(obct.money) from order_bill_cost_total obct where obct.cost_id = opbd.cost_id and obct.money_type='1' and obct.currency_code = 'OY') euro, (select SUM(obct.money) from order_bill_cost_total obct where obct.cost_id = opbd.cost_id and obct.money_type='1' and obct.currency_code = 'GB') hKDollar, (select pb.tax_rate from product_biz pb where pb.id_code = oi.biz_code) taxRate, opbd.remarks FROM order_payment_bill_detail opbd left join order_payment_bill opb on opbd.bill_id = opb.id left join order_info oi on oi.order_no = opbd.order_no left join order_payment_cost opc on opc.id = opbd.cost_id where opbd.bill_no = #{form.billNo} ) temp
and temp.orderNo = #{form.orderNo}
and temp.bizCode = #{form.bizCode}
and temp.startAddress like concat('%',#{form.startAddress},'%')
and temp.endAddress like concat('%',#{form.endAddress},'%')
and temp.costTypeName like concat('%',#{form.costTypeName},'%')
and temp.costName like concat('%',#{form.costName},'%')
and temp.costGenreId = #{form.costGenreId}
and temp.createdTimeStr <= #{form.endCreateTimeStr}
and temp.createdTimeStr <= #{form.beginCreateTimeStr}
