#{item}
GROUP BY opc.cost_code, opc.currency_code

SELECT CONCAT( opc.cost_code, opc.currency_code ) NAME, opc.main_order_no orderNo, concat(( SELECT ci.NAME FROM cost_info ci WHERE ci.id_code = opc.cost_code ), '(',( SELECT ci.currency_name FROM currency_info ci WHERE ci.currency_code = opc.currency_code ), ')' ) viewName, sum( opc.amount ) money FROM order_payment_cost opc WHERE opc.id IN
#{item}
GROUP BY opc.cost_code, opc.currency_code, opc.main_order_no

SELECT DATE_FORMAT( oi.create_time, '%Y-%m-%d' ) createdTimeStr, oi.order_no orderNo, oi.customer_name customerName, ( SELECT concat( da.city_name, da.address ) FROM delivery_address da LEFT JOIN order_take_adr ota on da.id = ota.delivery_id LEFT JOIN order_transport ot on ota.order_no = ot.order_no where ot.main_order_no = oi.order_no and ota.opr_type = '1') startAddress, ( SELECT concat( da.city_name, da.address ) FROM delivery_address da LEFT JOIN order_take_adr ota on da.id = ota.delivery_id LEFT JOIN order_transport ot on ota.order_no = ot.order_no where ot.main_order_no = oi.order_no and ota.opr_type = '2') endAddress, (select concat(osc.license_plate,',',osc.hk_license_plate) from order_send_cars osc left join order_transport ot on osc.order_no = ot.order_no where ot.main_order_no = oi.order_no) licensePlate, (select ot.vehicle_size from order_transport ot where ot.main_order_no = oi.order_no) vehicleSize, (select sum(ota.piece_amount) from order_take_adr ota left join order_transport ot on ota.order_no = ot.order_no where ot.main_order_no = oi.order_no and ota.opr_type = '1') pieceNum, (select sum(ota.weight) from order_take_adr ota left join order_transport ot on ota.order_no = ot.order_no where ot.main_order_no = oi.order_no and ota.opr_type = '1') weight, (select oc.yun_customs_no from order_customs oc where oc.main_order_no = oi.order_no) yunCustomsNo FROM order_info oi where oi.order_no in (select opc.order_no from order_payment_cost opc where opc.id in
#{item}
)
