mapper.select:
SELECT op.bill_no billNo, op.account_term accountTermStr, ( SELECT count( DISTINCT opbd.order_no ) FROM order_payment_bill_detail opbd WHERE opbd.bill_no = op.bill_no ) billNum, ( SELECT sum( obct.money ) FROM order_bill_cost_total obct WHERE op.bill_no = obct.bill_no AND obct.currency_code = 'CNY' ) rmb, ( SELECT sum( obct.money ) FROM order_bill_cost_total obct WHERE op.bill_no = obct.bill_no AND obct.currency_code = 'USD' ) dollar, ( SELECT sum( obct.money ) FROM order_bill_cost_total obct WHERE op.bill_no = obct.bill_no AND obct.currency_code = 'EUR' ) euro, ( SELECT sum( obct.money ) FROM order_bill_cost_total obct WHERE op.bill_no = obct.bill_no AND obct.currency_code = 'HKD' ) hKDollar, (select ifnull(sum(obct.local_money),0) from order_bill_cost_total obct where obct.bill_no = op.bill_no) localAmount, (select sum(cav.real_receive_money) from cancel_after_verification cav where cav.bill_no = op.bill_no) heXiaoAmount, op.audit_status billStatus, op.make_user makeUser, DATE_FORMAT(op.make_time,'%Y-%m-%d %H:%i:%S') makeTimeStr FROM order_payment_bill_detail op left join order_payment_bill opb on op.bill_id = opb.id where opb.legal_name = #{form.legalName} and opb.supplier_ch_name = #{form.supplierChName}
and opb.is_main = 1
-- TODO 2021-01-20 修改的不知道会不影响之前传梅版本财务
and opb.is_main = 0 and opb.sub_type = #{form.cmd}
group by bill_no

      SELECT
	    count( DISTINCT opbd.order_no ) billOrderNum
      FROM
	    order_payment_bill_detail opbd
	    LEFT JOIN order_payment_bill opb ON opbd.bill_id = opb.id
      WHERE
        opb.legal_name = #{legalName}
        AND opb.supplier_ch_name = #{supplierChName}
        AND opb.sub_type = #{subType}

      SELECT
	    ifnull(sum(opbd.local_amount),0) billOrderNum
      FROM
	    order_payment_bill_detail opbd
	    LEFT JOIN order_payment_bill opb ON opbd.bill_id = opb.id
      WHERE
        opb.legal_name = #{legalName}
        AND opb.supplier_ch_name = #{supplierChName}
        AND opb.sub_type = #{subType}
        AND opbd.audit_status!='edit_del'

      SELECT
	    count( DISTINCT opbd.bill_no ) billOrderNum
      FROM
	    order_payment_bill_detail opbd
	    LEFT JOIN order_payment_bill opb ON opbd.bill_id = opb.id
      WHERE
        opb.legal_name = #{legalName}
        AND opb.supplier_ch_name = #{supplierChName}
        AND opb.sub_type = #{subType}

SELECT CONCAT( opc.cost_code, opc.currency_code ) NAME, opc.main_order_no orderNo, opc.order_no subOrderNo, concat((SELECT ci.NAME FROM cost_info ci WHERE ci.id_code = opc.cost_code),'(',(SELECT ci.currency_name FROM currency_info ci WHERE ci.currency_code = opc.currency_code),')') viewName, sum( opc.amount ) money FROM order_payment_cost opc WHERE opc.id IN
#{item}
GROUP BY opc.cost_code, opc.currency_code, opc.main_order_no, opc.order_no union SELECT CONCAT( 'T', opc.currency_code ) NAME, opc.main_order_no orderNo, opc.order_no subOrderNo, concat('合计(',(SELECT ci.currency_name FROM currency_info ci WHERE ci.currency_code = opc.currency_code),')') viewName, sum( opc.amount ) money FROM order_payment_cost opc WHERE opc.id IN
#{item}
GROUP BY opc.currency_code, opc.main_order_no, opc.order_no

         SELECT
	    ifnull(sum(opbd.local_amount),0) billOrderNum
      FROM
	    order_payment_bill_detail opbd
	    LEFT JOIN order_payment_bill opb ON opbd.bill_id = opb.id
      WHERE
        opb.legal_entity_id = #{legalEntityId}
        AND opb.supplier_code = #{supplierCode}
        AND opb.sub_type = #{subType}
        AND opbd.audit_status!='edit_del'

         SELECT
	    count( DISTINCT opbd.order_no ) billOrderNum
      FROM
	    order_payment_bill_detail opbd
	    LEFT JOIN order_payment_bill opb ON opbd.bill_id = opb.id
      WHERE
        opb.legal_entity_id = #{legalEntityId}
        AND opb.supplier_code = #{supplierCode}
        AND opb.sub_type = #{subType}

         SELECT
	    count( DISTINCT opbd.bill_no ) billOrderNum
      FROM
	    order_payment_bill_detail opbd
	    LEFT JOIN order_payment_bill opb ON opbd.bill_id = opb.id
      WHERE
         opb.legal_entity_id = #{legalEntityId}
        AND opb.supplier_code = #{supplierCode}
        AND opb.sub_type = #{subType}
