mapper.select:
SELECT orbd.bill_no billNo, orb.legal_name legalName, orb.unit_account unitAccount, orbd.account_term accountTermStr, orbd.invoice_amount paymentAmount, orbd.settlement_currency settlementCurrency,orbd.audit_status auditStatus,orbd.apply_status applyStatus, orbd.make_user makeUser, orbd.make_time makeTimeStr, orbd.audit_user auditUser, orbd.audit_time auditTimeStr, orbd.push_kingdee_count pushKingdeeCount FROM order_receivable_bill_detail orbd left join (select t.bill_no,max(t.make_time) make_time from order_receivable_bill_detail t where t.audit_status != 'edit_no_commit' group by t.bill_no) tt on orbd.bill_no = tt.bill_no LEFT JOIN order_receivable_bill orb ON orbd.bill_id = orb.id where orbd.make_time = tt.make_time
and orb.is_main = 1
and orb.is_main = 1 and orbd.audit_status in ('B_1','B_2')
and (orbd.audit_status in ('B_3','B_4','B_5','B_5_1','B_6') or orbd.apply_status='4')
and orb.is_main = 0 and orb.sub_type = #{form.subType} and orbd.audit_status in ('B_1','B_2')
and orb.is_main = 0 and orb.sub_type = #{form.subType}
and orb.unit_account like concat('%',#{form.unitAccount},'%')
and orb.legal_name like concat('%',#{form.legalName},'%')
and orbd.bill_no like concat('%',#{form.billNo},'%')
and orbd.account_term = #{form.accountTermStr}
and orbd.settlement_currency = #{form.currencyCode}
and orbd.audit_status = #{form.auditStatus}
and orbd.apply_status = #{form.applyStatus}
and orbd.make_user like concat ('%',#{form.makeUser},'%')
and orb.legal_name in
#{legalName}
group by orbd.bill_no

select * from ( SELECT orbd.id billDetailId, oi.order_no orderNo, orc.order_no subOrderNo, oi.biz_code bizCode, (select pb.`name` from product_biz pb where pb.id_code = oi.biz_code) bizCodeDesc, oi.create_time createdTimeStr, orb.unit_account unitAccount, (SELECT GROUP_CONCAT(ota.goods_desc) FROM order_take_adr ota left join order_transport ot on ot.order_no = ota.order_no WHERE ot.main_order_no = oi.order_no and ota.opr_type = '1' GROUP BY ot.order_no) goodsDesc, (SELECT GROUP_CONCAT(concat(ifnull(da.city_name,''), da.address )) FROM delivery_address da LEFT JOIN order_take_adr ota ON da.id = ota.delivery_id left join order_transport ot on ot.order_no = ota.order_no WHERE ot.main_order_no = oi.order_no and ota.opr_type = '1' GROUP BY ot.order_no) startAddress, (SELECT GROUP_CONCAT(concat(ifnull(da.city_name,''), da.address )) FROM delivery_address da LEFT JOIN order_take_adr ota ON da.id = ota.delivery_id left join order_transport ot on ot.order_no = ota.order_no where ot.main_order_no = oi.order_no and ota.opr_type = '2' GROUP BY ot.order_no) endAddress, (select concat( vi.plate_number,' ',vi.hk_number) from order_send_cars osc left join order_transport ot on osc.order_no = ot.order_no left join vehicle_info vi on vi.id = osc.vehicle_id where ot.main_order_no = oi.order_no) licensePlate, (select GROUP_CONCAT(oc.yun_customs_no) from order_customs oc where oc.main_order_no = oi.order_no group by oc.main_order_no) yunCustomsNo, (select cg.`name` from cost_genre cg where cg.id = orc.cost_genre_id) costGenreName, orc.cost_genre_id, (select pb.cost_genre_ids from product_biz pb where pb.id_code = oi.biz_code) costGenreStr, orc.id costId, (select ct.code_name from cost_type ct where ct.id = orc.cost_type_id) costTypeName, (select ci.`name` from cost_info ci where ci.id_code = orc.cost_code) costName, (select SUM(obct.money) from order_bill_cost_total obct where obct.cost_id = orbd.cost_id and obct.money_type='2' and obct.currency_code = 'CNY') rmb, (select SUM(obct.money) from order_bill_cost_total obct where obct.cost_id = orbd.cost_id and obct.money_type='2' and obct.currency_code = 'USD') dollar, (select SUM(obct.money) from order_bill_cost_total obct where obct.cost_id = orbd.cost_id and obct.money_type='2' and obct.currency_code = 'EUR') euro, (select SUM(obct.money) from order_bill_cost_total obct where obct.cost_id = orbd.cost_id and obct.money_type='2' and obct.currency_code = 'HKD') hKDollar, ifnull(orbd.tax_rate,(select cg.tax_rate from cost_genre cg where cg.id = orc.cost_genre_id)) taxRate, orbd.remarks, orbd.bill_no, (select ci.currency_name from currency_info ci where ci.currency_code = orbd.settlement_currency) settlementCurrency, orbct.money settlementAmount,orbct.exchange_rate exchangeRate, orb.sub_type FROM order_receivable_bill_detail orbd left join order_receivable_bill orb on orbd.bill_id = orb.id left join order_info oi on oi.order_no = orbd.order_no left join order_receivable_cost orc on orc.id = orbd.cost_id left join order_bill_cost_total orbct on orbct.bill_no = orbd.bill_no and orbct.cost_id = orbd.cost_id and money_type='2' where orbd.bill_no = #{form.billNo} ) temp
and temp.orderNo = #{form.orderNo}
and temp.bizCode = #{form.bizCode}
and temp.startAddress like concat('%',#{form.startAddress},'%')
and temp.endAddress like concat('%',#{form.endAddress},'%')
and temp.costTypeName like concat('%',#{form.costTypeName},'%')
and temp.costName like concat('%',#{form.costName},'%')
and temp.cost_genre_id = #{form.costGenreId}
and temp.createdTimeStr <= #{form.endCreateTimeStr}
and temp.createdTimeStr <= #{form.beginCreateTimeStr}
