mapper.select:
SELECT ob.bill_no billNo, ob.account_term accountTermStr, ( SELECT count( DISTINCT orbd.order_no ) FROM order_receivable_bill_detail orbd WHERE ob.bill_no = orbd.bill_no ) billNum, ( SELECT ifnull(sum( obct.money ),0) FROM order_bill_cost_total obct WHERE ob.bill_no = obct.bill_no AND obct.currency_code = 'CNY' ) rmb, ( SELECT ifnull(sum( obct.money ),0) FROM order_bill_cost_total obct WHERE ob.bill_no = obct.bill_no AND obct.currency_code = 'USD' ) dollar, ( SELECT ifnull(sum( obct.money ),0) FROM order_bill_cost_total obct WHERE ob.bill_no = obct.bill_no AND obct.currency_code = 'EUR' ) euro, ( SELECT ifnull(sum( obct.money ),0) FROM order_bill_cost_total obct WHERE ob.bill_no = obct.bill_no AND obct.currency_code = 'HKD' ) hKDollar, (select ifnull(sum(obct.local_money),0) from order_bill_cost_total obct where obct.bill_no = ob.bill_no) localAmount, (select ifnull(sum(cav.real_receive_money),0) from cancel_after_verification cav where cav.bill_no = ob.bill_no) heXiaoAmount, ob.audit_status billStatus, ob.make_user makeUser, DATE_FORMAT(ob.make_time,'%Y-%m-%d %H:%i:%S') makeTimeStr FROM order_receivable_bill_detail ob left join order_receivable_bill orb on ob.bill_id = orb.id where orb.legal_name = #{form.legalName} and orb.unit_account = #{form.unitAccount}
and orb.is_main = 1
-- TODO 2021-01-20 修改的不知道会不影响之前传梅版本财务
and orb.is_main = 0 and orb.sub_type = #{form.cmd}
group by bill_no

      SELECT
	    count( DISTINCT orbd.order_no ) billOrderNum
      FROM
	    order_receivable_bill_detail orbd
	    LEFT JOIN order_receivable_bill orb ON orbd.bill_id = orb.id
      WHERE
        orb.legal_name = #{legalName}
        AND orb.unit_account = #{unitAccount}
        AND orb.sub_type = #{subType}

      SELECT
	    ifnull(sum(orbd.local_amount),0) billOrderNum
      FROM
	    order_receivable_bill_detail orbd
	    LEFT JOIN order_receivable_bill orb ON orbd.bill_id = orb.id
      WHERE
        orb.legal_name = #{legalName}
        AND orb.unit_account = #{unitAccount}
        AND orb.sub_type = #{subType}

      SELECT
	    count( DISTINCT orbd.bill_no ) billOrderNum
      FROM
	    order_receivable_bill_detail orbd
	    LEFT JOIN order_receivable_bill orb ON orbd.bill_id = orb.id
      WHERE
        orb.legal_name = #{legalName}
        AND orb.unit_account = #{unitAccount}
        AND orb.sub_type = #{subType}
