mapper.select:
SELECT ob.bill_no billNo, ob.account_term accountTermStr, ( SELECT count( DISTINCT orbd.order_no ) FROM order_receivable_bill_detail orbd WHERE ob.bill_no = orbd.bill_no ) billNum, ( SELECT ifnull(sum( obct.money ),0) FROM order_bill_cost_total obct WHERE ob.bill_no = obct.bill_no AND obct.currency_code = 'CNY' ) rmb, ( SELECT ifnull(sum( obct.money ),0) FROM order_bill_cost_total obct WHERE ob.bill_no = obct.bill_no AND obct.currency_code = 'USD' ) dollar, ( SELECT ifnull(sum( obct.money ),0) FROM order_bill_cost_total obct WHERE ob.bill_no = obct.bill_no AND obct.currency_code = 'EUR' ) euro, ( SELECT ifnull(sum( obct.money ),0) FROM order_bill_cost_total obct WHERE ob.bill_no = obct.bill_no AND obct.currency_code = 'HKD' ) hKDollar, (select ifnull(sum(obct.local_money),0) from order_bill_cost_total obct where obct.bill_no = ob.bill_no) localAmount, (select ifnull(sum(cav.real_receive_money),0) from cancel_after_verification cav where cav.bill_no = ob.bill_no) heXiaoAmount, ob.audit_status billStatus, ob.make_user makeUser, DATE_FORMAT(ob.make_time,'%Y-%m-%d %H:%i:%S') makeTimeStr FROM order_receivable_bill_detail ob left join order_receivable_bill orb on ob.bill_id = orb.id where orb.legal_name = #{form.legalName} and orb.unit_account = #{form.unitAccount}
and orb.is_main = 1
-- TODO 2021-01-20 修改的不知道会不影响之前传梅版本财务
and orb.is_main = 0 and orb.sub_type = #{form.cmd}
group by bill_no

select * from ( SELECT DISTINCT orc.main_order_no orderNo, orc.order_no subOrderNo, pb.`name` bizCodeDesc, DATE_FORMAT( oi.create_time, '%Y-%m-%d' ) createdTimeStr, c.`name` unitAccount,
cg.`name` costGenreName, ct.code_name costTypeName, ci.`name` costName, orc.amount, orc.currency_code, orc.change_amount localAmount, orc.id costId, orc.is_bill isBill
FROM order_receivable_cost orc LEFT JOIN order_info oi ON orc.main_order_no = oi.order_no left join customer_info c on c.id_code = orc.customer_code LEFT JOIN product_biz pb ON pb.id_code = oi.biz_code LEFT JOIN cost_genre cg ON orc.cost_genre_id = cg.id LEFT JOIN cost_type ct ON orc.cost_type_id = ct.id LEFT JOIN cost_info ci ON orc.cost_code = ci.id_code
LEFT JOIN order_transport sub ON sub.main_order_no = oi.order_no LEFT JOIN order_send_cars osc ON sub.order_no = osc.order_no left join order_customs oc on oc.order_no = orc.order_no left join vehicle_info vi on vi.id = osc.vehicle_id
left join ${dynamicSqlParam.table} sub on sub.order_no = orc.order_no
-- left join air_order ao on ao.order_no = orc.order_no -- left join sea_order so on so.order_no = orc.order_no -- left join order_inland_transport oit on oit.order_no = orc.order_no -- left join trailer_order tor on tor.order_no = orc.order_no -- left join storage_input_order sio on sio.order_no = orc.order_no -- left join storage_out_order soo on soo.order_no = orc.order_no WHERE orc.id NOT IN ( SELECT orbd.cost_id FROM order_receivable_bill_detail orbd where orbd.audit_status!='edit_del') and (orc.is_bill != '2' and orc.is_bill!='save_confirm') and orc.`status` = '3' and c.`name` = #{form.unitAccount}
and oi.legal_name = #{form.legalName} and orc.is_sum_to_main = 1
and orc.legal_name = #{form.legalName} and orc.is_sum_to_main = 0 and orc.sub_type = #{form.cmd}
and sub.legal_name = #{form.legalName} and orc.is_sum_to_main = 0 and orc.sub_type = #{form.cmd}
) temp where 1 = 1
and temp.startAddress like concat('%',#{form.startAddress},'%')
and temp.endAddress like concat('%',#{form.endAddress},'%')
and temp.costName like concat('%',#{form.costName},'%')
and temp.currency_code = #{form.currencyCode}
and date_format(temp.createdTimeStr,'%Y-%m-%d') <= date_format(#{form.endCreatedTimeStr},'%Y-%m-%d')
and date_format(temp.createdTimeStr,'%Y-%m-%d') >= date_format(#{form.beginCreatedTimeStr},'%Y-%m-%d')

      SELECT
	    count( DISTINCT orbd.order_no ) billOrderNum
      FROM
	    order_receivable_bill_detail orbd
	    LEFT JOIN order_receivable_bill orb ON orbd.bill_id = orb.id
      WHERE
        orb.legal_name = #{legalName}
        AND orb.unit_account = #{unitAccount}
        AND orb.sub_type = #{subType}

      SELECT
	    count( DISTINCT orbd.order_no ) billOrderNum
      FROM
	    order_receivable_bill_detail orbd
	    LEFT JOIN order_receivable_bill orb ON orbd.bill_id = orb.id
      WHERE
        orb.legal_entity_id = #{legalId}
        AND orb.unit_code = #{unitCode}
        AND orb.sub_type = #{subType}

      SELECT
	    ifnull(sum(orbd.local_amount),0) billOrderNum
      FROM
	    order_receivable_bill_detail orbd
	    LEFT JOIN order_receivable_bill orb ON orbd.bill_id = orb.id
      WHERE
        orb.legal_name = #{legalName}
        AND orb.unit_account = #{unitAccount}
        AND orb.sub_type = #{subType}
        AND orbd.audit_status!='edit_del'

      SELECT
	    ifnull(sum(orbd.local_amount),0) billOrderNum
      FROM
	    order_receivable_bill_detail orbd
	    LEFT JOIN order_receivable_bill orb ON orbd.bill_id = orb.id
      WHERE
        orb.legal_entity_id = #{legalId}
        AND orb.unit_code = #{unitCode}
        AND orb.sub_type = #{subType}
        AND orbd.audit_status!='edit_del'

      SELECT
	    count( DISTINCT orbd.bill_no ) billOrderNum
      FROM
	    order_receivable_bill_detail orbd
	    LEFT JOIN order_receivable_bill orb ON orbd.bill_id = orb.id
      WHERE
        orb.legal_name = #{legalName}
        AND orb.unit_account = #{unitAccount}
        AND orb.sub_type = #{subType}

      SELECT
	    count( DISTINCT orbd.bill_no ) billOrderNum
      FROM
	    order_receivable_bill_detail orbd
	    LEFT JOIN order_receivable_bill orb ON orbd.bill_id = orb.id
      WHERE
         orb.legal_entity_id = #{legalId}
        AND orb.unit_code = #{unitCode}
        AND orb.sub_type = #{subType}

select t1.main_order_no mainOrderNo, t1.order_no orderNo, t1.change_amount changeAmount from order_receivable_cost t1
and t1.is_bill in('0','1') and t1.is_sum_to_main = #{isMain} and t1.status = 3 and t1.customer_code = #{customerCode}
and t1.main_order_no in (select o1.order_no from order_info o1 where o1.legal_entity_id = #{legalEntityId})
and t1.legal_id = #{legalEntityId}
and t1.order_no in (select o1.order_no from ${dynamicSqlParam.table} o1 where o1.legal_entity_id = #{legalEntityId})
