SELECT DISTINCT oi.legal_name legalName, ci.unit_account unitAccount, ifnull( orb.already_paid_amount, 0 ) alreadyPaidAmount, ifnull( orb.bill_order_num, 0 ) billOrderNum, ifnull( orb.bill_num, 0 ) billNum, (select ifnull(sum(t1.change_amount),0) from order_receivable_cost t1 where t1.is_bill != '2' AND t1.is_sum_to_main = '0' and t1.customer_code = orc.customer_code and t1.main_order_no in (select o1.order_no from order_info o1 where o1.legal_name = oi.legal_name)) notPaidAmount, (select count(DISTINCT t1.main_order_no) from order_receivable_cost t1 where t1.is_bill != '2' AND t1.is_sum_to_main = '0' and t1.customer_code = orc.customer_code and t1.main_order_no in (select o1.order_no from order_info o1 where o1.legal_name = oi.legal_name)) notPaidOrderNum FROM order_receivable_cost orc left join customer_info ci on ci.unit_code = orc.customer_code LEFT JOIN order_info oi ON oi.order_no = orc.main_order_no left join order_receivable_bill orb on orb.legal_name = oi.legal_name and orb.unit_account = orc.customer_name where orc.is_sum_to_main = 0 and orc.sub_type = #{form.cmd}
and orb.unit_account like concat('%',#{form.unitAccount},'%')
and oi.legal_name like concat('%',#{form.legalName},'%')

      SELECT
	    count( DISTINCT orbd.order_no ) billOrderNum
      FROM
	    order_receivable_bill_detail orbd
	    LEFT JOIN order_receivable_bill orb ON orbd.bill_id = orb.id
      WHERE
        orb.legal_name = #{legalName}
        AND orb.unit_account = #{unitAccount}
        AND orb.sub_type = #{subType}

      SELECT
	    ifnull(sum(orbd.local_amount),0) billOrderNum
      FROM
	    order_receivable_bill_detail orbd
	    LEFT JOIN order_receivable_bill orb ON orbd.bill_id = orb.id
      WHERE
        orb.legal_name = #{legalName}
        AND orb.unit_account = #{unitAccount}
        AND orb.sub_type = #{subType}

      SELECT
	    count( DISTINCT orbd.bill_no ) billOrderNum
      FROM
	    order_receivable_bill_detail orbd
	    LEFT JOIN order_receivable_bill orb ON orbd.bill_id = orb.id
      WHERE
        orb.legal_name = #{legalName}
        AND orb.unit_account = #{unitAccount}
        AND orb.sub_type = #{subType}

