mapper.select:
select id, main_order_no, order_no, is_main, biz_type, legal_name, legal_id, department, department_id, customer_name, unit_code, unit_name, biz_uname, create_user, create_time, re_amount, re_equivalent_amount, pay_amount, pay_equivalent_amount, profit, re_in_amount, re_in_equivalent_amount, pay_in_amount, pay_in_equivalent_amount, in_profit, sync_time, re_cost_ids, pay_cost_ids from profit_statement
main_order_no like concat('%',#{form.mainOrderNo},'%')
order_no like concat('%',#{form.orderNo},'%')
biz_type =#{form.bizType}
legal_name =#{form.legalName}
department_id in
#{item}
customer_name like concat('%',#{form.customerName},'%')
unit_name like concat('%',#{form.unitName},'%')
biz_uname like concat('%',#{form.bizUname},'%')
create_user like concat('%',#{form.createUser},'%')
DATE_FORMAT( oi.create_time, '%Y-%m-%d' ) between #{form.createTime[0]} and #{form.createTime[1]}
is_main = #{form.statisticalType}
