mapper.select:
select t.id caseId,t.carton_no,t.fab_no, t1.id orderId,t1.order_no,t1.need_declare,t1.need_clearance, t2.company customerName, t3.good_id, t4.name_cn,t4.name_en,t4.image_url,t4.data_code,t4.material_quality,t4.purpose, t5.bill_id, t5.bill_no from order_case t join order_info t1 on t1.id = t.order_id join customer t2 on t2.id = t1.customer_id left join (select case_id,good_id from order_case_shop group by case_id) t3 on t3.case_id = t.id left join customer_goods t4 on t4.id = t3.good_id join counter_case_info t5 on t5.case_id = t.id where 1=1
and t5.bill_id = #{form.billId}
and t1.need_clearance = #{form.type}
and t.id not in
#{item}
order by t.id desc

select t.id caseId,t.carton_no,t.fab_no, t1.id orderId,t1.order_no,t1.need_declare,t1.need_clearance, t2.company customerName, t3.good_id, t4.name_cn,t4.name_en,t4.image_url,t4.data_code,t4.material_quality,t4.purpose, t5.bill_id, t5.bill_no from order_case t join order_info t1 on t1.id = t.order_id join customer t2 on t2.id = t1.customer_id left join (select case_id,good_id from order_case_shop group by case_id) t3 on t3.case_id = t.id left join customer_goods t4 on t4.id = t3.good_id left join clearance_info_case t5 on t5.case_id = t.id where 1=1
and t5.b_id = #{id}
order by t.id desc
