mapper.select:
select t.id,t.order_id,t.carton_no,t.fab_no, t.asn_length,t.asn_width,t.asn_height,t.asn_weight,t.asn_volume,t.asn_weigh_date, t.wms_length,t.wms_height,t.wms_width,t.wms_weight,t.wms_volume,t.wms_weigh_date, t.confirm_length,t.confirm_height,t.confirm_width,t.confirm_weight,t.confirm_volume,t.confirm_weigh_date, t.`status`,t.remark, CONCAT(t.asn_length,'*',t.asn_width,'*',t.asn_height) asn_lwg from order_case t where 1=1
and t.order_id = #{form.orderId}
and t.id not in
#{item}
and t.carton_no like concat('%',#{form.cartonNo},'%')
order by t.carton_no asc

select t.id,t.order_id,t.carton_no,t.fab_no, t.asn_length,t.asn_width,t.asn_height,t.asn_weight,t.asn_volume,t.asn_weigh_date, t.wms_length,t.wms_height,t.wms_width,t.wms_weight,t.wms_volume,t.wms_weigh_date, t.confirm_length,t.confirm_height,t.confirm_width,t.confirm_weight,t.confirm_volume,t.confirm_weigh_date, t.`status`,t.remark, CONCAT(t.asn_length,'*',t.asn_width,'*',t.asn_height) asn_lwg from order_case t join counter_case_info cci on cci.order_id = t.order_id and cci.case_id = t.id where 1=1
and cci.b_id = #{form.counterListInfoId}
and cci.order_id = #{form.orderId}
and t.carton_no like concat('%',#{form.cartonNo},'%')
order by t.carton_no asc
