mapper.select:
select t.id,t.order_no,t.customer_id,t.store_goods_warehouse_code,t.store_goods_warehouse_name,t.destination_warehouse_code,t.destination_warehouse_name, t.is_pick,t.create_time,t.create_user_id,t.create_user_name,t.order_origin,t.bol_no,t.need_declare,t.need_clearance,t.remark, t.charge_weight,t.volume_weight,t.actual_weight,t.actual_volume,t.total_cartons,t.amazon_reference_id,t.extension_number,t.sales_commission,t.front_status_code,t.front_status_name,t.after_status_code,t.after_status_name,t.clearing_way,t.fill_material_description,t.warehouse_no, t1.company, t2.names,t2.sail_time,t2.cut_off_time, t4.code_name serviceName, oc.id confId from order_info t join customer t1 on t1.id = t.customer_id join offer_info t2 on t2.id = t.offer_info_id join quotation_template t3 on t3.id = t2.qie join service_group t4 on t4.id = t3.sid JOIN ocean_conf_detail ocd on ocd.id_code = t2.id and ocd.types=1 JOIN order_conf oc on oc.id = ocd.order_id WHERE 1 = 1
and oc.id = #{form.orderConfId}
and t.id not in
#{item}
and t.order_no like concat('%',#{form.orderNo},'%')

select t.id,t.order_no,t.customer_id,t.store_goods_warehouse_code,t.store_goods_warehouse_name,t.destination_warehouse_code,t.destination_warehouse_name, t.is_pick,t.create_time,t.create_user_id,t.create_user_name,t.order_origin,t.bol_no,t.need_declare,t.need_clearance,t.remark, t.charge_weight,t.volume_weight,t.actual_weight,t.actual_volume,t.total_cartons,t.amazon_reference_id,t.extension_number,t.sales_commission,t.front_status_code,t.front_status_name,t.after_status_code,t.after_status_name,t.clearing_way,t.fill_material_description,t.warehouse_no, t1.company, t2.names,t2.sail_time,t2.cut_off_time, t4.code_name serviceName from order_info t join customer t1 on t1.id = t.customer_id join offer_info t2 on t2.id = t.offer_info_id join quotation_template t3 on t3.id = t2.qie join service_group t4 on t4.id = t3.sid join counter_order_info coi on coi.order_id = t.id where 1=1
and coi.b_id = #{form.counterListInfoId}
and t.order_no like concat('%',#{form.orderNo},'%')
