mapper.select:
select
    t1.id,t1.warehouse_code,t1.warehouse_name,t1.contacts,t1.company_name,t1.contact_number,t1.contact_phone,t1.address_first,t1.address_second,t1.address_thirdly,t1.zip_code,t1.status,t1.user_id,t1.user_name,t1.create_time,t1.area_group,t1.country_code,t1.country_name,t1.state_code,t1.state_name,t1.city_code,t1.city_name,t1.region_code,t1.region_name
from (
select
DISTINCT t1.id,substring_index( substring_index(t1.warehouseId,',',t2.id + 1), ',' ,-1 ) as warehouseId
from (select id,arrive_warehouse warehouseId from quotation_template where id = #{qie}) t1 join help_index t2 on t2.id  <  (length(t1.warehouseId) - length(REPLACE(t1.warehouseId,',','')) + 1 )
) t
left join fab_warehouse t1 on t1.id = t.warehouseId;
