mapper.select:
select t.id,t.tid,t.supplier_id,t.order_id,t.start_code,t.end_code,t.sail_time,t.voyage_day,t.unit,t.create_time,t.task_id,t.operation_team_id,t.user_id,t.user_name,t.bill_name,t.bill_remark, t1.code_name tname, t2.company_name supplierName, t3.code_name startName, t4.code_name endName, t5.group_name taskName, t6.group_name operationTeamName, CONCAT(t3.code_name,'/',t4.code_name) startEndName from ocean_bill t left join transport_way t1 on t1.id = t.tid left join supplier_info t2 on t2.id = t.supplier_id left join harbour_info t3 on t3.id_code = t.start_code left join harbour_info t4 on t4.id_code = t.end_code left join task_group t5 on t5.id = t.task_id left join operation_team t6 on t6.id = t.operation_team_id where 1=1 /*报价名*/
AND t.`order_id` LIKE concat('%',#{form.orderId},'%')
/*开船日期*/
and t.sail_time >= concat(date_format(#{form.sailTime}, '%Y-%m-%d'), ' 00:00:00') AND t.sail_time <= concat(date_format(#{form.sailTime}, '%Y-%m-%d'), ' 23:59:59')
/*供应商*/
and t.supplier_id = #{form.supplierId}
/*目的港*/
and t4.state_code = #{form.destinationCountryCode}
/*运输方式*/
and t.tid = #{form.tid}

select t.id as case_id,t.carton_no,t.asn_weight,t.asn_volume, t1.order_no,t1.need_declare,t1.need_clearance,t1.customer_id, t2.company as customerName from order_case t join order_info t1 on t1.id = t.order_id join customer t2 on t2.id = t1.customer_id join offer_info t3 on t3.id = t1.offer_info_id join ocean_conf_detail t4 on t4.id_code = t3.id and t4.types = 1 join order_conf t5 on t5.id = t4.order_id where t5.id = #{form.confId}
and t.carton_no = #{form.cartonNo}
and t1.order_no = #{form.orderNo}
