mapper.select:
select t.id, t.qie, t.`names`, t.sail_time, t.cut_off_time, t.jc_time, t.jkc_time, t.types, t.`status`, t.user_id, t.user_name, t.create_time from offer_info t WHERE 1 = 1
AND t.`names` LIKE concat('%',#{form.names},'%')
and t.sail_time >= #{form.sailTimeStart} AND t.sail_time <= #{form.sailTimeEnd}
and t.cut_off_time >= #{form.cutOffTimeStart} AND t.cut_off_time <= #{form.cutOffTimeEnd}
GROUP BY t.`id`

select t.id,t.qie,t.`names`,t.sail_time,t.cut_off_time,t.jc_time,t.jkc_time,t.types,t.status,t.user_id,t.user_name,t.create_time,t.estimated_time,t.remarks, t1.start_shipment,t1.destination_port, t3.code_name startShipmentName, t4.code_name destinationPortName, CONCAT(t3.code_name,'---->',t4.code_name) shipRoute, DATEDIFF(t.estimated_time,t.sail_time) voyage_day from offer_info t LEFT JOIN quotation_template t1 on t1.id = t.qie LEFT JOIN service_group t2 on t2.id = t1.sid LEFT JOIN harbour_info t3 on t3.id_code = t1.start_shipment LEFT JOIN harbour_info t4 on t4.id_code = t1.destination_port WHERE 1 = 1
AND t4.state_code = #{form.stateCode}
AND t1.tid = #{form.tid}
AND t.types = #{form.types}
