mapper.select:
select t.id,t.order_no,t.customer_id,t.store_goods_warehouse_code,t.store_goods_warehouse_name,t.destination_warehouse_code,t.destination_warehouse_name, t.is_pick,t.`status`,t.status_name,t.create_time,t.create_user_id,t.create_user_name,t.order_origin,t.bol_no,t.need_declare,t.need_clearance,t.remark, t.charge_weight,t.volume_weight,t.actual_weight,t.actual_volume,t.total_cartons, t1.company, t2.names,t2.sail_time,t2.cut_off_time, t3.taskItems taskTodo, t4.group_name from order_info t left join customer t1 on t1.id = t.customer_id left join offer_info t2 on t2.id = t.offer_info_id left join ( SELECT t.order_info_id, GROUP_CONCAT(t.task_name order by sort asc) AS taskItems FROM waybill_task_relevance t where t.status != 3 GROUP BY t.order_info_id ) t3 on t3.order_info_id = t.id left join operation_team t4 on t4.id = t1.operation_team_id WHERE 1 = 1
AND t.customer_id = #{form.customerId}
AND t.order_no LIKE CONCAT('%',#{form.orderNo},'%')
and t.destination_warehouse_code = #{form.destinationWarehouseCode}
and t.status = #{form.status}

select t.id,t.order_no,t.customer_id,t.offer_info_id,t.reserve_size,t.store_goods_warehouse_code,t.store_goods_warehouse_name,t.destination_warehouse_code, t.destination_warehouse_name,t.is_pick,t.status,t.status_name,t.create_time,t.create_user_id,t.create_user_name,t.order_origin, t.bol_no,t.need_declare,t.need_clearance,t.remark,t.charge_weight,t.volume_weight,t.actual_weight,t.actual_volume,t.total_cartons, t1.names,t1.types,t1.sail_time,t1.cut_off_time,t1.jc_time,t1.jkc_time,t1.estimated_time,DATEDIFF(t1.estimated_time,t1.sail_time) voyage_day,t1.remarks, t2.start_shipment,t2.destination_port, t3.code_name startShipmentName,t3.cname startShipmentCityName, t4.code_name destinationPortName, t1.qie, t5.quantity quantityShipped from order_info t left join offer_info t1 on t1.id = t.offer_info_id left join quotation_template t2 on t2.id = t1.qie left join harbour_info t3 on t3.id_code = t2.start_shipment left join harbour_info t4 on t4.id_code = t2.destination_port left join (select t5.order_id,count(t5.order_id) quantity from order_case t5 group by t5.order_id) t5 on t5.order_id = t.id where 1=1
and t.customer_id = #{form.customerId}
and t.status = #{form.status}
and t.order_no = #{form.orderNo}

select count(t.id) draftNum from order_info t where 1=1
and t.customer_id = #{form.customerId}
and t.status = 0

select
t3.tdId,t3.id,t3.tid,t3.supplier_id,t3.order_id,t3.start_code,t3.end_code,t3.sail_time,t3.voyage_day,t3.unit,t3.create_time,t3.task_id,t3.operation_team_id
from order_conf t
join (
select order_id pzId from ocean_conf_detail where types=1 and id_code=#{offerInfoId}
) t1 on t1.pzId = t.id
left join (
select order_id pzId,id_code tdId from ocean_conf_detail where types=2
) t2 on t2.pzId = t.id
left join (
select id tdId,id, tid, supplier_id, order_id, start_code, end_code, sail_time, voyage_day, unit, create_time, task_id, operation_team_id from ocean_bill
) t3 on t3.tdId = t2.tdId
