mapper.select:
select t.id,t.bill_code,t.order_id,t.legal_person_id,t.supplier_id,t.amount,t.cid,t.remarks,t.status,t.create_time, t1.order_no, t2.legal_entity, t3.company_name supplierName, t4.currency_code,t4.currency_name, CONCAT(t.amount, ' ', t4.currency_name) amountFormat from pay_bill_master t left join order_info t1 on t1.id = t.order_id left join legal_person t2 on t2.id = t.legal_person_id left join supplier_info t3 on t3.id = t.supplier_id left join currency_info t4 on t4.id = t.cid where 1=1
and t.bill_code = #{form.billCode}
and t1.order_no = #{form.orderNo}
and t.supplier_id = #{form.supplierId}
and t.status = #{form.status}

select
t.id,t.bill_code,t.order_id,t.legal_person_id,t.supplier_id,t.amount,t.cid,t.remarks,t.status,t.create_time,t.payer_id,t.payment_time,t.voucher_url,t.account_payable_id
from pay_bill_master t
where 1=1
and t.create_time  >=  #{firstday, jdbcType=TIMESTAMP}
and t.create_time  <=  #{lastDay, jdbcType=TIMESTAMP}
