mapper.select:
select t.id,t.types,t.sid,t.`names`,t.pic_url,t.tid,t.start_shipment,t.destination_port, t.arrive_warehouse,t.visible_customer,t.visible_uid,t.sail_time,t.cut_off_time,t.jc_time,t.jkc_time,t.estimated_time,t.gidtype,t.gid,t.area_id,t.qidtype,t.qid,t.task_id,t.bubble_coefficient, t.remarks,t.`status`,t.user_id,t.user_name,t.create_time,t.update_time,t.billing_weight_unit,t.whole_time,t.design_formulas,t.volume,t.volume_unit,t.minimum_quantity, t1.code_name sname, t2.code_name tname, t3.code_name startShipmentName, t4.code_name destinationPortName, t5.group_name taskName, t6.name destinationCountryName from quotation_template t LEFT JOIN service_group t1 on t1.id = t.sid LEFT JOIN transport_way t2 on t2.id = t.tid LEFT JOIN harbour_info t3 on t3.id_code = t.start_shipment LEFT JOIN harbour_info t4 on t4.id_code = t.destination_port LEFT JOIN task_group t5 on t5.id = t.task_id left join country t6 on t6.code = t4.state_code WHERE 1 = 1
AND t.`names` LIKE concat('%',#{form.names},'%')
and t.sail_time >= concat(date_format(#{form.sailTime}, '%Y-%m-%d'), ' 00:00:00') AND t.sail_time <= concat(date_format(#{form.sailTime}, '%Y-%m-%d'), ' 23:59:59')
and t.cut_off_time >= concat(date_format(#{form.cutOffTime}, '%Y-%m-%d'), ' 00:00:00') AND t.cut_off_time <= concat(date_format(#{form.cutOffTime}, '%Y-%m-%d'), ' 23:59:59')
and t6.code = #{form.countryCode}
and t.`status` = #{form.status}
