mapper.select:
select t.id,t.supplier_info_id,t.service_id,t.cost_item_id,t.count_way,t.amount,t.amount_unit,t.amount_source,t.unit_price,t.cid,t.remark,t.status,t.user_id,t.user_name,t.create_time, t1.company_name supplierInfoName, t2.serve_name, t3.cost_name costItemName, t4.currency_code,t4.currency_name from supplier_cost t left join supplier_info t1 on t1.id = t.supplier_info_id left join supplier_serve t2 on t2.id = t.service_id left join cost_item t3 on t3.id = t.cost_item_id left join currency_info t4 on t4.id = t.cid where 1=1
and t.id = #{form.id}
and t.supplier_info_id = #{form.supplierInfoId}
and t.service_id = #{form.serviceId}
and t.cost_item_id = #{form.costItemId}
