mapper.select:
select
t.id,t.task_type,t.task_id,t.operation_team_id,t.member_user_id,t.`status`,t.creator,t.creation_time,
t1.task_code billTaskCode,t1.task_name billTaskName,
t2.task_code waybillTaskCode,t2.task_name waybillTaskName,
t3.group_code operationGroupCode,t3.group_name operationGroupName,
t4.`name` memberUserName
from
task_member_relation t
LEFT JOIN bill_task t1 on t1.id = t.task_id AND t.task_type = 1
LEFT JOIN waybill_task t2 on t2.id = t.task_id AND t.task_type = 2
LEFT JOIN operation_team t3 on t3.id = t.operation_team_id
LEFT JOIN system_user t4 on t4.id = t.member_user_id
WHERE
1=1

    AND t.id = #{form.id}


    AND t.task_type = #{form.taskType}


    AND t.task_id = #{form.taskId}


    AND t.operation_team_id = #{form.operationTeamId}


    AND t.member_user_id = #{form.memberUserId}


    AND t.`status` = #{form.status}
