mapper.select:
select t.id,t.customer_id,t.trading_no,t.trading_type,t.amount,t.cid,t.serialNumber,t.voucher_url,t.status,t.remark,t.creator,t.create_time,t.auditor,t.audit_time, t1.company customerName, t2.currency_code,t2.currency_name, t3.user_name creatorName, t4.name auditorName from trading_record t left join customer t1 on t1.id = t.customer_id left join currency_info t2 on t2.id = t.cid left join customer t3 on t3.id = t.creator left join system_user t4 on t4.id = t.auditor where 1=1 and t.trading_type = 1
and t.customer_id = #{form.customerId}
and t.serialNumber LIKE concat('%',#{form.serialNumber},'%')
and t.status = #{form.status}
and t.cid = #{form.cid}

select t.id,t.customer_id,t.trading_no,t.trading_type,t.amount,t.cid,t.serialNumber,t.voucher_url,t.status,t.remark,t.creator,t.create_time,t.auditor,t.audit_time, t1.company customerName, t2.currency_code,t2.currency_name, t3.user_name creatorName, t4.name auditorName from trading_record t left join customer t1 on t1.id = t.customer_id left join currency_info t2 on t2.id = t.cid left join customer t3 on t3.id = t.creator left join system_user t4 on t4.id = t.auditor where 1=1
and t.customer_id = #{form.customerId}
and t.trading_no LIKE concat('%',#{form.tradingNo},'%')
and t.trading_type = #{form.tradingType}
and t.cid = #{form.cid}
