mapper.select:
select t.id, t.work_no, t.order_id, t.order_no, t.priority, t.problem_description, t.file_url, t.status, t.type, t.customer_id, t.submission_time, t.operator, t.operation_time, t.revert, t.customer_evaluation, t1.user_name customerAccount from work_order t left join customer t1 on t1.id = t.customer_id where 1=1
and t.customer_id = #{form.customerId}
and t.work_no like concat('%',#{form.workNo},'%')
and t.submission_time >= #{form.submissionTimeStart} and t.submission_time <= #{form.submissionTimeEnd}
and ( t.work_no like concat('%',#{form.keyword},'%') or t.problem_description like concat('%',#{form.keyword},'%') )
