mapper.select:
select so.id,so.main_order_no mainOrderNo,so.order_no orderNo,so.status, so.process_status processStatus, so.unit_code unitCode, so.legal_name legalName, so.legal_entity_id legalEntityId, so.imp_and_exp_type impAndExpType, so.terms, so.port_departure_code portDepartureCode, sp.name portDepartureName, so.port_destination_code portDestinationCode, sp1.name portDestinationName, so.good_time goodTime, sb.id sid, sb.sea_order_no, sb.status bstatus, sb.sea_order_id, sb.ship_company shipCompany, sb.ship_name shipName, sb.ship_number shipNumber, sb.eta,sb.etd,sb.ata,sb.atd, sb.bill_lading_weight, sb.main_no,sb.sub_no, sb.warehousing_no warehousingNo, sb.delivery_wharf, sb.agent_supplier_id supplierId, sb.file_path, sb.file_name, so.create_user createUser, so.create_time, so.update_user, so.update_time, so.need_input_cost, so.unit_code unitCode, so.unit_department_id, so.cut_replenish_time, so.cabinet_number, so.paper_strip_seal, so.cabinet_size, so.cabinet_type, so.is_freight_collect, so.is_other_expenses_paid, so.is_dangerous_goods, so.is_charged, so.cabinet_size_name, so.cabinet_type_name, terms.name termsDesc, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.order_no = so.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.order_no = so.`order_no`)>0 THEN TRUE ELSE FALSE END) cost from sea_order so left join sea_port sp on so.port_departure_code = sp.code left join sea_port sp1 on so.port_destination_code = sp1.code left join sea_bookship sb on sb.sea_order_id = so.id and (sb.`status` != 2 OR sb.`status` IS NULL) left join terms on so.terms = terms.id
and so.order_no like concat('%',#{form.orderNo},'%')
and sp.name like concat('%',#{form.portDeparture},'%')
and sp1.name like concat('%',#{form.portDestination},'%')
and so.main_order_no IN
#{item}
and so.status = #{form.status}
and so.process_status IN
#{item}
and so.create_time = #{form.createTime}
AND (EXISTS (SELECT 1 FROM order_payment_cost opc WHERE opc.`order_no` = so.order_no AND opc.`status` = '2' and opc.sub_type = 'hy') OR EXISTS (SELECT 1 FROM order_receivable_cost orc WHERE orc.`order_no` = so.order_no AND orc.`status` = '2' and orc.sub_type = 'hy'))
and so.`legal_entity_id` in
#{legalId}
order by so.id desc
