mapper.select:
select so.id,so.main_order_no mainOrderNo,so.order_no orderNo,so.status, so.process_status processStatus, so.unit_code unitCode, so.legal_name legalName, so.legal_entity_id legalEntityId, so.imp_and_exp_type impAndExpType, so.terms, so.port_departure_code portDepartureCode, sp.name portDepartureName, so.port_destination_code portDestinationCode, sp1.name portDestinationName, so.transit_port_code transitPortCode, sp2.name transitPort, so.good_time goodTime, sb.id sid, sb.sea_order_no, sb.status bstatus, sb.sea_order_id, sb.ship_company shipCompany, sb.ship_name shipName, sb.ship_number shipNumber, sb.eta,sb.etd,sb.ata,sb.atd, sb.warehousing_no warehousingNo, sb.delivery_wharf, sb.agent_supplier_id supplierId, sb.file_path, sb.file_name, sb.closing_time closingTime, so.create_user createUser, so.create_time, so.update_user, so.update_time, so.need_input_cost, so.unit_code unitCode, so.unit_department_id, so.cut_replenish_time, so.cabinet_size, so.cabinet_type, so.is_freight_collect, so.is_other_expenses_paid, so.is_dangerous_goods, so.is_charged, so.cabinet_size_name, so.cabinet_type_name, so.audit_opinion, so.audit_status, so.department_id departmentId, so.place_of_delivery placeOfDelivery, so.cut_off_time cutOffTime, so.closing_time closingTime, so.so so, so.destination destination, terms.name termsDesc, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.order_no = so.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.order_no = so.`order_no`)>0 THEN TRUE ELSE FALSE END) cost from sea_order so left join sea_port sp on so.port_departure_code = sp.code left join sea_port sp1 on so.port_destination_code = sp1.code left join sea_port sp2 on so.transit_port_code = sp2.code left join sea_bookship sb on sb.sea_order_id = so.id and (sb.`status` != 2 OR sb.`status` IS NULL) left join terms on so.terms = terms.id
and so.order_no like concat('%',#{form.orderNo},'%')
and so.main_order_no like concat('%',#{form.mainOrderNo},'%')
and sp.name like concat('%',#{form.portDeparture},'%')
and sp1.name like concat('%',#{form.portDestination},'%')
and so.main_order_no IN
#{item}
and so.status = #{form.status}
and so.process_status IN
#{item}
and so.create_time >= #{form.startTime}
and so.create_time <= #{form.endTime}
AND (EXISTS (SELECT 1 FROM order_payment_cost opc WHERE opc.`order_no` = so.order_no AND opc.`status` = '2' and opc.sub_type = 'hy') OR EXISTS (SELECT 1 FROM order_receivable_cost orc WHERE orc.`order_no` = so.order_no AND orc.`status` = '2' and orc.sub_type = 'hy'))
AND so.order_no in ( (SELECT opc.order_no FROM order_payment_cost opc WHERE opc.`status` = '3' and opc.sub_type='hy' and opc.is_bill='0' union SELECT orc.order_no FROM order_receivable_cost orc WHERE orc.`status` = '3' and orc.sub_type='hy' and orc.is_bill='0') )
and so.`legal_entity_id` in
#{legalId}
order by so.id desc

select count(*) from sea_order so LEFT JOIN order_info oi ON so.`main_order_no` = oi.`order_no`
and so.status=#{status} AND oi.`status` in ('1','4')
and so.status=#{status} AND terms in (5,6)
and so.status=#{status}
and so.`legal_entity_id` in
#{legalId}

select so.id,so.main_order_no mainOrderNo,so.order_no orderNo,so.status, so.process_status processStatus,so.unit_code unitCode,so.legal_name, so.legal_entity_id,so.imp_and_exp_type, so.terms,so.port_departure_code,so.port_destination_code,so.transit_port_code, so.good_time,so.create_user,so.create_time,so.update_user,so.update_time, so.need_input_cost,so.unit_code unitCode,so.unit_department_id, so.cut_replenish_time,so.cabinet_size,so.cabinet_type,so.is_freight_collect, so.is_other_expenses_paid,so.is_dangerous_goods,so.is_charged,so.cabinet_size_name, so.cabinet_type_name, so.audit_opinion,so.audit_status, t.name termsDesc, -- 订舱 sb.id bId, sb.sea_order_no,sb.status bStatus,sb.sea_order_id,sb.ship_company,sb.ship_name, sb.ship_number,sb.eta,sb.etd,sb.ata,sb.atd, sb.warehousing_no,sb.delivery_wharf,sb.agent_supplier_id,sb.file_path, sb.file_name,sb.closing_time, -- 柜型 csn.id csnId, csn.sea_order_id,csn.sea_order_no,csn.cabinet_type_size,csn.number -- 柜号 sci.id sciId, sci.sea_order_no,sci.sea_rep_id,sci.sea_rep_no,sci.cabinet_name, sci.cabinet_number,sci.paper_strip_seal,sci.weight,sci.volume, sci.plat_number,sci.packing, -- 补料 sr.id srId, sr.order_no srOrderNo, sr.sea_order_id, sr.sea_order_no, sr.cut_replenish_time srCutReplenishTime, sr.cabinet_number srCabinetNumber, sr.paper_strip_seal srPaperStripSeal, sr.is_bill_of_lading, sr.imp_and_exp_type srImpAndExpType, sr.terms srTerms, sr.port_departure_code srPortDepartureCode, sr.port_destination_code srPortDestinationCode, sr.good_time srGoodTime, sr.is_freight_collect srIsFreightCollect, sr.is_other_expenses_paid srIsOtherExpensesPaid, sr.is_dangerous_goods srIsDangerousGoods, sr.is_charged srIsCharged, sr.transit_port_code srTransitPortCode, sr.cabinet_type srCabinetType, sr.cabinet_type_name srCabinetTypeName,sr.main_no srMainNo,sr.sub_no srSubNo, sr.bill_lading_weight srBillLadingWeight,sr.is_release_order srIsReleaseOrder from sea_order so left join sea_bookship sb on sb.sea_order_id = so.id left join terms t on so.terms = t.id left join cabinet_size_number csn on csn.sea_order_id=so.id left join sea_container_information sci on sci.sea_order_no=so.order_no left join sea_replenishment sr on sr.sea_order_id = so.id
so.main_order_no in
#{item}
