mapper.select:
select sr.id,sr.order_no orderNo,sea_order_id seaOrderId,sea_order_no seaOrderNo, sr.cut_replenish_time cutReplenishTime,sr.cabinet_number cabinetNumber, sr.paper_strip_seal paperStripSeal,sr.create_user createUser,sr.create_time createTime, sr.update_user updateUser,sr.update_time updateTime,sr.is_bill_of_lading isBillOfLading, sr.is_release_order isReleaseOrder, sr.imp_and_exp_type impAndExpType,sr.terms, terms.name termsDesc, sr.port_departure_code portDepartureCode, sp.name portDepartureName, sr.port_destination_code portDestinationCode, sp1.name portDestinationName, sr.good_time goodTime,sr.is_freight_collect isFreightCollect, sr.is_other_expenses_paid isOtherExpensesPaid, sr.is_dangerous_goods isDangerousGoods,sr.is_charged isCharged, sr.transit_port_code transitPortCode, sp2.name transitPort, sr.cabinet_type cabinetType, sr.cabinet_type_name cabinetTypeName, sr.main_no,sr.sub_no,sr.bill_lading_weight from sea_replenishment sr left join sea_port sp on sr.port_departure_code = sp.code left join sea_port sp1 on sr.port_destination_code = sp1.code left join sea_port sp2 on sr.transit_port_code = sp2.code left join terms on sr.terms = terms.id left join sea_order so on so.id = sr.sea_order_id
and so.order_no like concat('%',#{form.orderNo},'%')
and sp.name like concat('%',#{form.portDeparture},'%')
and sp1.name like concat('%',#{form.portDestination},'%')
and so.main_order_no IN
#{item}
and so.status = #{form.status}
and sr.is_bill_of_lading = 0
and sr.is_release_order = 0
and so.process_status IN
#{item}
and sr.create_time >= #{form.startTime}
and sr.create_time <= #{form.endTime}
and so.`legal_entity_id` in
#{legalId}
order by sr.id desc

select count(*) from sea_replenishment sr left join sea_order so on so.id = sr.sea_order_id LEFT JOIN order_info oi ON so.`main_order_no` = oi.`order_no`
and so.status=#{status} AND sr.is_bill_of_lading = 0
and so.status=#{status} AND sr.is_release_order = 0
and so.status=#{status}
and so.`legal_entity_id` in
#{legalId}
