mapper.select:
select id, line_code, line_name, line_type, audit_status, route_attribute, passing, line_route, mileage, prescription, average_mileage, remarks, created_user, create_time, up_time, up_user, from_address, to_address, ( case audit_status when 1 then '待审核' when 2 then '审核通过' when 3 then '终止' when 0 then '拒绝' end ) as auditStatusDesc from line
and audit_status = #{form.auditStatus}
and line_name like concat('%',#{form.lineName},'%')
and date_format(create_time,'%Y-%m-%d') BETWEEN #{form.createTime[0]} AND #{form.createTime[1]}
order by create_time desc
