mapper.select:
SELECT * FROM ( SELECT DISTINCT oi.id, oi.`order_no` orderNo, oi.`biz_code` bizCode, oc.`goods_type` goodsType, oc.`port_code` portCode, oc.`port_name` portName, oi.`status`, oi.`class_code` classCode, pc1.name classCodeDesc, oi.`customer_name` customerName, oi.`customer_code` customerCode, oi.`legal_name` legalName, '' goodsInfo, '' takeAddress, '' giveAddress, oc.`status` subCustomsStatus, '' subTmsStatus, oi.`created_user` createdUser, DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') createdTimeStr, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.main_order_no = oi.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.main_order_no = oi.`order_no`)>0 THEN TRUE ELSE FALSE END) isCost, oi.`need_input_cost` needInputCost, oi.`customs_release` customsRelease FROM order_info oi LEFT JOIN order_customs oc ON oi.`order_no` = oc.`main_order_no` LEFT JOIN product_classify pc1 ON pc1.`id_code` = oi.`class_code` WHERE oi.`class_code` = 'CBG' UNION ALL SELECT DISTINCT oi.id, oi.`order_no` orderNo, oi.`biz_code` bizCode, ot.`goods_type` goodsType, ot.`port_code` portCode, p.`name` portName, oi.`status`, oi.`class_code` bizCode, pc2.name bizDesc, oi.`customer_name` customerName, oi.`customer_code` customerCode, oi.`legal_name` legalName, (SELECT CONCAT(ota.`goods_desc`,' ',ota.`piece_amount`,'件',',','重量',ota.`weight`,'KG') FROM order_take_adr ota WHERE ota.`order_no` = ot.order_no AND ota.`opr_type` = 1 ORDER BY ota.`id` LIMIT 1) goodsInfo, (SELECT da.`address` FROM order_take_adr ota LEFT JOIN delivery_address da ON ota.`delivery_id` = da.`id` WHERE ota.`order_no` = ot.order_no AND ota.`opr_type` = 1 ORDER BY ota.`id` LIMIT 1) takeAddress, (SELECT da.`address` FROM order_take_adr ota LEFT JOIN delivery_address da ON ota.`delivery_id` = da.`id` WHERE ota.`order_no` = ot.order_no AND ota.`opr_type` = 2 ORDER BY ota.`id` LIMIT 1) giveAddress, (SELECT oc.`status` FROM order_customs oc WHERE oc.`main_order_no`= oi.`order_no` LIMIT 1) subCustomsStatus, ot.`status` subTmsStatus, oi.`created_user` createdUser, DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') createdTimeStr, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.main_order_no = oi.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.main_order_no = oi.`order_no`)>0 THEN TRUE ELSE FALSE END) isCost, oi.`need_input_cost` needInputCost, oi.`customs_release` customsRelease FROM order_info oi LEFT JOIN order_transport ot ON oi.`order_no` = ot.`main_order_no` LEFT JOIN port_info p ON ot.port_code = p.id_code LEFT JOIN product_classify pc2 ON pc2.id_code = oi.`class_code` WHERE oi.`class_code` = 'ZGYS' ) temp WHERE 1 = 1
and temp.`status` = 2
and temp.`status` IN (1,4) AND (EXISTS (SELECT 1 FROM order_payment_cost opc WHERE opc.`main_order_no` = temp.orderNo AND opc.`status` = '2') OR EXISTS (SELECT 1 FROM order_receivable_cost orc WHERE orc.`main_order_no` = temp.orderNo AND orc.`status` = '2'))
and temp.classCode = 'ZGYS' and temp.`status` = 1 and temp.customsRelease = 0 and not exists (SELECT 1 FROM order_customs oc WHERE oc.`main_order_no` = temp.orderNo)
and temp.`status` = 4
and temp.orderNo = #{form.orderNo}
and temp.portCode = #{form.portCode}
and temp.classCode = #{form.classCode}
and temp.customerName like concat('%',#{form.customerName},'%')
and temp.legalName like concat('%',#{form.legalName},'%')
and temp.createdTimeStr <= #{form.endCreateTimeStr}
and temp.createdTimeStr >= #{form.beginCreateTimeStr}

SELECT DISTINCT oi.id, oi.`order_no` orderNo, ot.`goods_type` goodsType, ot.`port_code` portCode, p.`name` portName, oi.`status`, oi.`class_code` bizCode, pc2.name bizDesc, oi.`customer_name` customerName, oi.`customer_code` customerCode, oi.`legal_name` legalName, (SELECT CONCAT(ota.`goods_desc`,' ',ota.`piece_amount`,'件',',','重量',ota.`weight`,'KG') FROM order_take_adr ota WHERE ota.`order_no` = ot.order_no AND ota.`opr_type` = 1 ORDER BY ota.`id` LIMIT 1) goodsInfo, (SELECT da.`address` FROM order_take_adr ota LEFT JOIN delivery_address da ON ota.`delivery_id` = da.`id` WHERE ota.`order_no` = ot.order_no AND ota.`opr_type` = 1 ORDER BY ota.`id` LIMIT 1) takeAddress, (SELECT da.`address` FROM order_take_adr ota LEFT JOIN delivery_address da ON ota.`delivery_id` = da.`id` WHERE ota.`order_no` = ot.order_no AND ota.`opr_type` = 2 ORDER BY ota.`id` LIMIT 1) giveAddress, (SELECT oc.`status` FROM order_customs oc WHERE oc.`main_order_no`= oi.`order_no` LIMIT 1) subCustomsStatus, ot.`status` subTmsStatus, oi.`created_user` createdUser, DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') createdTimeStr, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.main_order_no = oi.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.main_order_no = oi.`order_no`)>0 THEN TRUE ELSE FALSE END) isCost, oi.`need_input_cost` needInputCost, oi.`customs_release` customsRelease FROM order_info oi LEFT JOIN order_transport ot ON oi.`order_no` = ot.`main_order_no` LEFT JOIN port_info p ON ot.port_code = p.id_code LEFT JOIN product_classify pc2 ON pc2.id_code = oi.`class_code` WHERE oi.`class_code` = 'ZGYS' AND ot.`status` = 'T_6' AND (oi.`customs_release` = 1 OR ((SELECT COUNT(1) FROM logistics_track lt WHERE lt.`main_order_id` = oi.`id` AND lt.`status`='C_5') = (SELECT COUNT(1) FROM order_customs oc WHERE oc.`main_order_no` = oi.`order_no`) AND (SELECT COUNT(1) FROM logistics_track lt WHERE lt.`main_order_id` = oi.`id` AND lt.`status`='C_5') > 0) )
and oi.order_no = #{form.orderNo}
and ot.port_code = #{form.portCode}
and oi.class_code = #{form.classCode}
and oi.customer_name like concat('%',#{form.customerName},'%')
and temp.legal_name like concat('%',#{form.legalName},'%')
and DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') <= #{form.endCreateTimeStr}
and DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') >= #{form.beginCreateTimeStr}
