mapper.select:
select * from ( SELECT oi.id, oi.`order_no` orderNo, (CASE oc.`goods_type` WHEN 1 THEN '进口' WHEN 2 THEN '出口' END) goodsTypeDesc, oc.`port_code` portCode, oc.`port_name` portName, oi.`status`, (CASE oi.`status` WHEN 1 THEN '正常' WHEN 2 THEN '草稿' WHEN 3 THEN '关闭' END) statusDesc, oi.`biz_code` bizCode, (SELECT pc.name FROM product_classify pc WHERE pc.id_code = oi.`biz_code`) bizDesc, oi.`customer_name` customerName, '' goodsInfo, '' takeAddress, '' giveAddress, oi.`created_user` createdUser, DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H %I %S') createdTimeStr FROM order_info oi LEFT JOIN order_customs oc ON oi.`order_no` = oc.`main_order_no` UNION ALL SELECT oi.id, oi.`order_no` orderNo, (CASE ot.`goods_type` WHEN 1 THEN '进口' WHEN 2 THEN '出口' END) goodsTypeDesc, ot.`port_code` portCode, ot.`port_name` portName, oi.`status`, (CASE oi.`status` WHEN 1 THEN '正常' WHEN 2 THEN '草稿' WHEN 3 THEN '关闭' END) statusDesc, oi.`biz_code` bizCode, (SELECT pc.name FROM product_classify pc WHERE pc.id_code = oi.`biz_code`) bizDesc, oi.`customer_name` customerName, (SELECT CONCAT(ota.`describe`,' ',ota.`amount`,(CASE ota.`unit` WHEN 1 THEN '件' WHEN 2 THEN '其他' END),',','重量',ota.`weight`,'KG') FROM order_take_adr ota WHERE ota.`order_no` = ot.order_no AND ota.`types` = 1 ORDER BY ota.`id` LIMIT 1) goodsInfo, (SELECT da.`address` FROM order_take_adr ota LEFT JOIN delivery_address da ON ota.`delivery_id` = da.`id` WHERE ota.`order_no` = ot.order_no AND ota.`types` = 1 ORDER BY ota.`id` LIMIT 1) takeAddress, (SELECT da.`address` FROM order_take_adr ota LEFT JOIN delivery_address da ON ota.`delivery_id` = da.`id` WHERE ota.`order_no` = ot.order_no AND ota.`types` = 2 ORDER BY ota.`id` LIMIT 1) giveAddress, oi.`created_user` createdUser, DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H %I %S') createdTimeStr FROM order_info oi LEFT JOIN order_transport ot ON oi.`order_no` = ot.`main_order_no` ) temp WHERE 1 = 1
and temp.orderNo = #{form.orderNo}
and temp.portCode = #{form.portCode}
and temp.bizCode = #{form.bizCode}
