SELECT DISTINCT oi.id, oi.`remarks`, oi.`order_no` orderNo, ot.`goods_type` goodsType, ot.`port_code` portCode, oi.`status`, oi.`class_code` classCode, oi.`customer_name` customerName, oi.`customer_code` customerCode, oi.`legal_name` legalName,
ot.`status` subTmsStatus, oi.`created_user` createdUser, DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') createdTimeStr,
oi.`need_input_cost` needInputCost, oi.`customs_release` customsRelease, oi.selected_server selectedServer FROM order_info oi LEFT JOIN order_transport ot ON oi.`order_no` = ot.`main_order_no`
WHERE -- TODO 外部报关 (oi.`class_code` = 'ZGYS' or oi.selected_server like concat('%','ZGYSDD','%')) AND (ot.`status` = 'T_6' or (ot.`status` = 'T_5' AND ot.is_vehicle_weigh = 0)) AND (oi.`customs_release` = 1 OR -- TODO 出口报关所有都放行才能进通关前审核 ((SELECT COUNT(1) FROM logistics_track lt WHERE lt.`main_order_id` = oi.`id` AND lt.`status`='C_5') = (SELECT COUNT(1) FROM order_customs oc WHERE oc.`main_order_no` = oi.`order_no`) AND (SELECT COUNT(1) FROM logistics_track lt WHERE lt.`main_order_id` = oi.`id` AND lt.`status`='C_5') > 0) )
and oi.order_no = #{form.orderNo}
and ot.port_code = #{form.portCode}
and oi.class_code = #{form.classCode}
and oi.customer_name like concat('%',#{form.customerName},'%')
and temp.legal_name like concat('%',#{form.legalName},'%')
and DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') <= #{form.endCreateTimeStr}
and DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') >= #{form.beginCreateTimeStr}
and oi.created_user like concat('%',#{form.createdUser},'%')
and oi.`legal_entity_id` in
#{legalId}

