FROM
DUAL

SELECT count(*) FROM order_info oi LEFT JOIN order_transport ot ON oi.`order_no` = ot.`main_order_no` WHERE -- TODO 外部报关 (oi.`class_code` = 'ZGYS' or oi.selected_server like concat('%','ZGYSDD','%')) AND (ot.`status` = 'T_6' or (ot.`status` = 'T_5' AND ot.is_vehicle_weigh = 0)) AND (oi.`customs_release` = 1 OR -- TODO 出口报关所有都放行才能进通关前审核 ((SELECT COUNT(1) FROM logistics_track lt WHERE lt.`main_order_id` = oi.`id` AND lt.`status`='C_5') = (SELECT COUNT(1) FROM order_customs oc WHERE oc.`main_order_no` = oi.`order_no`) AND (SELECT COUNT(1) FROM logistics_track lt WHERE lt.`main_order_id` = oi.`id` AND lt.`status`='C_5') > 0) )
and oi.`legal_entity_id` in
