mapper.select:
select distinct * from ( SELECT oi.id, oi.`order_no` orderNo, (CASE oc.`goods_type` WHEN 1 THEN '进口' WHEN 2 THEN '出口' END) goodsTypeDesc, oc.`port_code` portCode, oc.`port_name` portName, oi.`status`, (CASE oi.`status` WHEN 1 THEN '正常' WHEN 2 THEN '草稿' WHEN 3 THEN '关闭' END) statusDesc, oi.`class_code` classCode, (SELECT pc.name FROM product_classify pc WHERE pc.id_code = oi.`class_code`) classCodeDesc, oi.`customer_name` customerName, oi.`customer_code` customerCode, '' goodsInfo, '' takeAddress, '' giveAddress, oi.`created_user` createdUser, DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%I:%S') createdTimeStr, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.main_order_no = oi.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.main_order_no = oi.`order_no`)>0 THEN TRUE ELSE FALSE END) isCost FROM order_info oi,order_customs oc WHERE oi.`order_no` = oc.`main_order_no` UNION ALL SELECT oi.id, oi.`order_no` orderNo, (CASE ot.`goods_type` WHEN 1 THEN '进口' WHEN 2 THEN '出口' END) goodsTypeDesc, ot.`port_code` portCode, ot.`port_name` portName, oi.`status`, (CASE oi.`status` WHEN 1 THEN '正常' WHEN 2 THEN '草稿' WHEN 3 THEN '关闭' END) statusDesc, oi.`class_code` bizCode, (SELECT pc.name FROM product_classify pc WHERE pc.id_code = oi.`class_code`) bizDesc, oi.`customer_name` customerName, oi.`customer_code` customerCode, (SELECT CONCAT(ota.`describe`,' ',ota.`amount`,(CASE ota.`unit` WHEN 1 THEN '件' WHEN 2 THEN '其他' END),',','重量',ota.`weight`,'KG') FROM order_take_adr ota WHERE ota.`order_no` = ot.order_no AND ota.`types` = 1 ORDER BY ota.`id` LIMIT 1) goodsInfo, (SELECT da.`address` FROM order_take_adr ota LEFT JOIN delivery_address da ON ota.`delivery_id` = da.`id` WHERE ota.`order_no` = ot.order_no AND ota.`types` = 1 ORDER BY ota.`id` LIMIT 1) takeAddress, (SELECT da.`address` FROM order_take_adr ota LEFT JOIN delivery_address da ON ota.`delivery_id` = da.`id` WHERE ota.`order_no` = ot.order_no AND ota.`types` = 2 ORDER BY ota.`id` LIMIT 1) giveAddress, oi.`created_user` createdUser, DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%I:%S') createdTimeStr, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.main_order_no = oi.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.main_order_no = oi.`order_no`)>0 THEN TRUE ELSE FALSE END) isCost FROM order_info oi,order_transport ot WHERE oi.`order_no` = ot.`main_order_no` ) temp WHERE 1 = 1
and temp.`status` = 2
and temp.`status` in (1,3)
and temp.`status` = 1 AND (EXISTS (SELECT 1 FROM order_payment_cost opc WHERE opc.`main_order_no` = temp.orderNo) OR EXISTS (SELECT 1 FROM order_receivable_cost orc WHERE orc.`main_order_no` = temp.orderNo))
and temp.orderNo = #{form.orderNo}
and temp.portCode = #{form.portCode}
and temp.classCode = #{form.classCode}
