mapper.select:
SELECT oi.id, oi.remarks, oi.`order_no` orderNo, oi.`biz_code` bizCode, oi.`customer_name` customerName, oi.`status`, oi.`legal_name` legalName, oi.`legal_entity_id` legal_entity_id, oi.unit_account, oi.contract_no, oi.reference_no, oi.biz_uname, -- p.`name` portName, oi.`class_code` classCode, pc2.name classCodeDesc, oi.selected_server selectedServer, oi.`customer_code` customerCode, oi.`created_user` createdUser, oi.`create_time` createdTimeStr, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.main_order_no = oi.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.main_order_no = oi.`order_no`)>0 THEN TRUE ELSE FALSE END) isCost, oi.`need_input_cost` needInputCost, oi.`customs_release` customsRelease, oi.unit_code unitCode, oi.reject_comment, oi.is_rejected, oi.operation_time FROM order_info oi LEFT JOIN product_classify pc2 ON pc2.id_code = oi.`class_code`
and oi.`status` = 2
and oi.`status` IN (1,3,4) AND (EXISTS (SELECT 1 FROM order_payment_cost opc WHERE opc.`main_order_no` = oi.order_no AND opc.`status` = '2' and opc.sub_type='main') OR EXISTS (SELECT 1 FROM order_receivable_cost orc WHERE orc.`main_order_no` = oi.order_no AND orc.`status` = '2' and orc.sub_type='main'))
and oi.`status` IN (1,3,4) AND oi.order_no in ( (SELECT opc.main_order_no FROM order_payment_cost opc WHERE opc.`status` = '3' and opc.sub_type='main' and opc.is_bill='0' union SELECT orc.main_order_no FROM order_receivable_cost orc WHERE orc.`status` = '3' and orc.sub_type='main' and orc.is_bill='0') )
and (oi.class_code = 'ZGYS' or oi.selected_server like concat('%','ZGYSDD','%')) and oi.`status` = 1 and oi.customs_release = 0 and oi.selected_server not like concat('%','CKBG','%')
and oi.`status` = 4
and oi.`status` = 6
and oi.`status` = 7
and oi.order_no like concat('%',#{form.orderNo},'%')
and oi.class_code = #{form.classCode}
and oi.customer_name like concat('%',#{form.customerName},'%')
and oi.legal_name like concat('%',#{form.legalName},'%')
and oi.create_time <= #{form.endCreateTimeStr}
and oi.create_time >= #{form.beginCreateTimeStr}
and oi.created_user like concat('%',#{form.createdUser},'%')
and oi.operation_time between #{form.operationTime[0]} and #{form.operationTime[1]}
and oi.created_user = #{form.loginUserName}
and oi.`legal_entity_id` in
#{legalId}

SELECT DISTINCT oi.id, oi.`remarks`, oi.`order_no` orderNo, oi.`status`, oi.`class_code` classCode, oi.`customer_name` customerName, oi.`customer_code` customerCode, oi.`legal_name` legalName, oi.`created_user` createdUser, oi.`create_time` createdTimeStr, oi.`need_input_cost` needInputCost, oi.`customs_release` customsRelease, oi.selected_server selectedServer, oi.operation_time FROM order_info oi
oi.`id` in
#{item}
-- TODO 为空返回null值 oi.id=-1
and oi.order_no = #{form.orderNo}
and ot.port_code = #{form.portCode}
and oi.class_code = #{form.classCode}
and oi.customer_name like concat('%',#{form.customerName},'%')
and oi.legal_name like concat('%',#{form.legalName},'%')
and DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') <= #{form.endCreateTimeStr}
and DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') >= #{form.beginCreateTimeStr}
and oi.created_user like concat('%',#{form.createdUser},'%')
and oi.operation_time between #{form.operationTime[0]} and #{form.operationTime[1]}
order by oi.id desc

SELECT
(SELECT
COUNT(1)
FROM
order_info o1



            o1.created_user = #{form.loginUserName}



                o1.`legal_entity_id` in

                    #{legalId}





) AS allCount,

(SELECT
COUNT(1)
FROM
order_info o2
WHERE o2.status = '2'


        and o2.created_user = #{form.loginUserName}



            and o2.`legal_entity_id` in

                #{legalId}




) AS preSubmitCount,

(SELECT
COUNT(1)
FROM
order_info o3
WHERE o3.status = '4'


        and o3.created_user = #{form.loginUserName}



            and o3.`legal_entity_id` in

                #{legalId}




) AS dataNotAllCount,

(SELECT
COUNT(1)
FROM
order_info o6
WHERE o6.status = '6'


        and o6.created_user = #{form.loginUserName}



            and o6.`legal_entity_id` in

                #{legalId}




) AS cancelledCount,

(SELECT
COUNT(1)
FROM
order_info o7
WHERE o7.status = '7'


        and o7.created_user = #{form.loginUserName}



            and o7.`legal_entity_id` in

                #{legalId}




) AS pendingRejectedCount
FROM
DUAL

SELECT count(*) FROM order_info oi LEFT JOIN order_transport ot ON oi.`order_no` = ot.`main_order_no` WHERE -- TODO 外部报关 (oi.`class_code` = 'ZGYS' or oi.selected_server like concat('%','ZGYSDD','%')) AND (ot.`status` = 'T_6' or (ot.`status` = 'T_5' AND ot.is_vehicle_weigh = 0)) AND (oi.`customs_release` = 1 OR -- TODO 出口报关所有都放行才能进通关前审核 ((SELECT COUNT(1) FROM logistics_track lt WHERE lt.`main_order_id` = oi.`id` AND lt.`status`='C_5') = (SELECT COUNT(1) FROM order_customs oc WHERE oc.`main_order_no` = oi.`order_no`) AND (SELECT COUNT(1) FROM logistics_track lt WHERE lt.`main_order_id` = oi.`id` AND lt.`status`='C_5') > 0) )
and oi.`legal_entity_id` in
#{legalId}
