SELECT * FROM ( SELECT DISTINCT oi.id, oi.`order_no` orderNo, oi.`biz_code` bizCode, oc.`goods_type` goodsType, oc.`port_code` portCode, oc.`port_name` portName, oi.`status`, oi.`class_code` classCode, pc1.name classCodeDesc, oi.`customer_name` customerName, oi.`customer_code` customerCode, oi.`legal_name` legalName, '' goodsInfo, '' takeAddress, '' giveAddress, (select GROUP_CONCAT(t.`status`) from order_customs t where t.main_order_no = oi.order_no group by t.main_order_no) subCustomsStatus, '' subTmsStatus, '' subAirStatus, oi.`created_user` createdUser, DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') createdTimeStr, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.main_order_no = oi.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.main_order_no = oi.`order_no`)>0 THEN TRUE ELSE FALSE END) isCost, oi.`need_input_cost` needInputCost, oi.`customs_release` customsRelease, (select ai.audit_comment from audit_info ai where ai.ext_id = oc.id and ai.audit_status = 'C_1_1' order by ai.created_time desc limit 1) rejectComment FROM order_info oi LEFT JOIN order_customs oc ON oi.`order_no` = oc.`main_order_no` LEFT JOIN product_classify pc1 ON pc1.`id_code` = oi.`class_code` WHERE oi.`class_code` = 'CBG' UNION ALL SELECT DISTINCT oi.id, oi.`order_no` orderNo, oi.`biz_code` bizCode, ot.`goods_type` goodsType, ot.`port_code` portCode, p.`name` portName, oi.`status`, oi.`class_code` classCode, pc2.name classCodeDesc, oi.`customer_name` customerName, oi.`customer_code` customerCode, oi.`legal_name` legalName, (SELECT CONCAT(ota.`goods_desc`,' ',ota.`piece_amount`,'件',',','重量',ota.`weight`,'KG') FROM order_take_adr ota WHERE ota.`order_no` = ot.order_no AND ota.`opr_type` = 1 ORDER BY ota.`id` LIMIT 1) goodsInfo, (SELECT concat(ifnull(da.state_name,''),ifnull(da.city_name,''),da.address) FROM order_take_adr ota LEFT JOIN delivery_address da ON ota.`delivery_id` = da.`id` WHERE ota.`order_no` = ot.order_no AND ota.`opr_type` = 1 ORDER BY ota.`id` LIMIT 1) takeAddress, (SELECT concat(ifnull(da.state_name,''),ifnull(da.city_name,''),da.address) FROM order_take_adr ota LEFT JOIN delivery_address da ON ota.`delivery_id` = da.`id` WHERE ota.`order_no` = ot.order_no AND ota.`opr_type` = 2 ORDER BY ota.`id` LIMIT 1) giveAddress, (SELECT oc.`status` FROM order_customs oc WHERE oc.`main_order_no`= oi.`order_no` LIMIT 1) subCustomsStatus, ot.`status` subTmsStatus, '' subAirStatus, oi.`created_user` createdUser, DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') createdTimeStr, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.main_order_no = oi.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.main_order_no = oi.`order_no`)>0 THEN TRUE ELSE FALSE END) isCost, oi.`need_input_cost` needInputCost, oi.`customs_release` customsRelease, '' rejectComment FROM order_info oi LEFT JOIN order_transport ot ON oi.`order_no` = ot.`main_order_no`
LEFT JOIN port_info p ON ot.port_code = p.id_code LEFT JOIN product_classify pc2 ON pc2.id_code = oi.`class_code` WHERE oi.`class_code` = 'ZGYS' -- 空运 UNION ALL SELECT DISTINCT oi.id, oi.`order_no` orderNo, oi.`biz_code` bizCode, a.`imp_and_exp_type` goodsType, '' portCode, '' portName, oi.`status`, oi.`class_code` classCode, pc2.name classCodeDesc, oi.`customer_name` customerName, oi.`customer_code` customerCode, oi.`legal_name` legalName, '' goodsInfo, (SELECT address FROM order_address oa where oa.`business_id` = a.id AND oa.`type` = 0 ORDER BY oa.`id` LIMIT 1) takeAddress, (SELECT address FROM order_address oa where oa.`business_id` = a.id AND oa.`type` = 1 ORDER BY oa.`id` LIMIT 1) giveAddress, (SELECT oc.`status` FROM order_customs oc WHERE oc.`main_order_no`= oi.`order_no` LIMIT 1) subCustomsStatus, (SELECT ot.`status` FROM order_transport ot WHERE ot.`main_order_no`= oi.`order_no` LIMIT 1) subTmsStatus, a.`status` subAirStatus, oi.`created_user` createdUser, DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') createdTimeStr, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.main_order_no = oi.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.main_order_no = oi.`order_no`)>0 THEN TRUE ELSE FALSE END) isCost, oi.`need_input_cost` needInputCost, oi.`customs_release` customsRelease, '' rejectComment FROM order_info oi LEFT JOIN air_order a ON oi.`order_no` = a.`main_order_no` LEFT JOIN product_classify pc2 ON pc2.id_code = oi.`class_code` WHERE oi.`class_code` = 'KY' -- 服务单 UNION ALL SELECT DISTINCT oi.id, oi.`order_no` orderNo, oi.`biz_code` bizCode, '' goodsType, '' portCode, '' portName, oi.`status`, oi.`class_code` classCode, pc2.name classCodeDesc, oi.`customer_name` customerName, oi.`customer_code` customerCode, oi.`legal_name` legalName, '' goodsInfo, (SELECT da.address FROM order_take_adr ota left join delivery_address da on ota.`delivery_id` = da.id WHERE ota.`order_no` = so.order_no AND ota.`opr_type` = 1 ORDER BY ota.`id` LIMIT 1) takeAddress, (SELECT da.address FROM order_take_adr ota left join delivery_address da on ota.`delivery_id` = da.id WHERE ota.`order_no` = so.order_no AND ota.`opr_type` = 2 ORDER BY ota.`id` LIMIT 1) giveAddress, '' subCustomsStatus, '' subTmsStatus, '' subAirStatus, oi.`created_user` createdUser, DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') createdTimeStr, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.main_order_no = oi.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.main_order_no = oi.`order_no`)>0 THEN TRUE ELSE FALSE END) isCost, oi.`need_input_cost` needInputCost, oi.`customs_release` customsRelease, '' rejectComment FROM order_info oi LEFT JOIN service_order so ON oi.`order_no` = so.`main_order_no` LEFT JOIN product_classify pc2 ON pc2.id_code = oi.`class_code` WHERE oi.`class_code` = 'FWD'
) temp WHERE 1 = 1
and temp.`status` = 2
and temp.`status` IN (1,4) AND (EXISTS (SELECT 1 FROM order_payment_cost opc WHERE opc.`main_order_no` = temp.orderNo AND opc.`status` = '2' and opc.sub_type='main') OR EXISTS (SELECT 1 FROM order_receivable_cost orc WHERE orc.`main_order_no` = temp.orderNo AND orc.`status` = '2' and orc.sub_type='main'))
and temp.classCode = 'ZGYS' and temp.`status` = 1 and temp.customsRelease = 0 and not exists (SELECT 1 FROM order_customs oc WHERE oc.`main_order_no` = temp.orderNo)
and temp.`status` = 4
and temp.`status` = 6
and temp.`status` = 7
and temp.orderNo = #{form.orderNo}
and temp.portCode = #{form.portCode}
and temp.classCode = #{form.classCode}
and temp.customerName like concat('%',#{form.customerName},'%')
and temp.legalName like concat('%',#{form.legalName},'%')
and temp.createdTimeStr <= #{form.endCreateTimeStr}
and temp.createdTimeStr >= #{form.beginCreateTimeStr}
and temp.createdUser = #{form.loginUserName}

SELECT DISTINCT oi.id, oi.`order_no` orderNo, ot.`goods_type` goodsType, ot.`port_code` portCode, p.`name` portName, oi.`status`, oi.`class_code` classCode, pc2.name classCodeDesc, oi.`customer_name` customerName, oi.`customer_code` customerCode, oi.`legal_name` legalName, (SELECT CONCAT(ota.`goods_desc`,' ',ota.`piece_amount`,'件',',','重量',ota.`weight`,'KG') FROM order_take_adr ota WHERE ota.`order_no` = ot.order_no AND ota.`opr_type` = 1 ORDER BY ota.`id` LIMIT 1) goodsInfo, (SELECT concat(ifnull(da.state_name,''),ifnull(da.city_name,''),da.address) FROM order_take_adr ota LEFT JOIN delivery_address da ON ota.`delivery_id` = da.`id` WHERE ota.`order_no` = ot.order_no AND ota.`opr_type` = 1 ORDER BY ota.`id` LIMIT 1) takeAddress, (SELECT concat(ifnull(da.state_name,''),ifnull(da.city_name,''),da.address) FROM order_take_adr ota LEFT JOIN delivery_address da ON ota.`delivery_id` = da.`id` WHERE ota.`order_no` = ot.order_no AND ota.`opr_type` = 2 ORDER BY ota.`id` LIMIT 1) giveAddress, (SELECT oc.`status` FROM order_customs oc WHERE oc.`main_order_no`= oi.`order_no` LIMIT 1) subCustomsStatus, ot.`status` subTmsStatus, oi.`created_user` createdUser, DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') createdTimeStr, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.main_order_no = oi.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.main_order_no = oi.`order_no`)>0 THEN TRUE ELSE FALSE END) isCost, oi.`need_input_cost` needInputCost, oi.`customs_release` customsRelease, oi.selected_server selectedServer FROM order_info oi LEFT JOIN order_transport ot ON oi.`order_no` = ot.`main_order_no` LEFT JOIN service_order so ON oi.`order_no` = so.`main_order_no` LEFT JOIN port_info p ON ot.port_code = p.id_code LEFT JOIN product_classify pc2 ON pc2.id_code = oi.`class_code` WHERE oi.`class_code` = 'ZGYS' AND (ot.`status` = 'T_6' or (ot.`status` = 'T_5' AND ot.is_vehicle_weigh = 0)) AND (oi.`customs_release` = 1 OR ((SELECT COUNT(1) FROM logistics_track lt WHERE lt.`main_order_id` = oi.`id` AND lt.`status`='C_5') = (SELECT COUNT(1) FROM order_customs oc WHERE oc.`main_order_no` = oi.`order_no`) AND (SELECT COUNT(1) FROM logistics_track lt WHERE lt.`main_order_id` = oi.`id` AND lt.`status`='C_5') > 0) )
and oi.order_no = #{form.orderNo}
and ot.port_code = #{form.portCode}
and oi.class_code = #{form.classCode}
and oi.customer_name like concat('%',#{form.customerName},'%')
and temp.legal_name like concat('%',#{form.legalName},'%')
and DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') <= #{form.endCreateTimeStr}
and DATE_FORMAT(oi.`create_time`,'%Y-%m-%d %H:%i:%S') >= #{form.beginCreateTimeStr}
