mapper.select:
SELECT distinct opc.`id`, opc.main_order_no, opc.`customer_code` customerCode , (select si.supplier_ch_name from supplier_info si where si.supplier_code = opc.customer_code limit 1)customerName, opc.`cost_type_id` costTypeId, (SELECT ct.code_name FROM cost_type ct WHERE ct.id = opc.`cost_type_id` LIMIT 1) costType, opc.`cost_genre_id` costGenreId, (SELECT cg.name FROM cost_genre cg WHERE cg.id = opc.cost_genre_id LIMIT 1) costGenre, opc.`unit`, opc.`cost_code` costCode, (SELECT ci.name FROM cost_info ci WHERE ci.id_code = opc.`cost_code` LIMIT 1) costName, opc.`unit_price` unitPrice, opc.`number`, opc.`currency_code` currencyCode, (SELECT ci.currency_name FROM currency_info ci WHERE ci.currency_code = opc.`currency_code` LIMIT 1) currencyName, opc.`amount`, opc.`change_amount` changeAmount, opc.`exchange_rate` exchangeRate, opc.`remarks`, opc.`status`, opc.unloading_address unloadingAddress, opc.supplier_id,opc.department_id,opc.is_internal,opc.is_sum_to_main,opc.sub_type, opc.receivable_id,opc.is_reimbursement
FROM order_payment_cost opc where opc.`main_order_no` = #{form.mainOrderNo}
and (opc.sub_type = 'main' or (opc.is_sum_to_main = 1 and opc.`status` = '3'))
and ((opc.`status` in ('2','3') and opc.sub_type = 'main') or (opc.`status` in ('3') and opc.sub_type !='main' and opc.is_sum_to_main=true))
and ((opc.`status` in ('3') and opc.sub_type = 'main' and opc.is_bill='0') or (opc.`status` in ('3') and opc.sub_type !='main' and opc.is_sum_to_main=true and opc.is_bill='0'))
and opc.sub_type = #{form.subType} and opc.order_no = #{form.subOrderNo}
and opc.`status` in ('2','3') and opc.sub_type = #{form.subType} and opc.order_no = #{form.subOrderNo}
and opc.`status` = '3' and opc.sub_type = #{form.subType} and opc.order_no = #{form.subOrderNo} and opc.is_bill='0'
and opc.supplier_id = #{form.supplierId}
order by opc.customer_code, opc.cost_code, opc.created_time desc;

choose.when:
SELECT opc.main_order_no mainOrderNo, count( 1 ) FROM `order_payment_cost` opc left join order_info oi on opc.main_order_no=oi.order_no WHERE opc.sub_type = "main" AND opc.`status` = 2 AND oi.`status` IN (1,3,4)
and main_order_no in
#{item}
and oi.`legal_entity_id` in
#{companyId}
and oi.biz_belong_depart in
#{item}
and opc.created_user = #{userName}
GROUP BY opc.main_order_no

choose.otherwise:
SELECT opc.order_no orderNo, count( 1 ) FROM `order_payment_cost` opc left join order_info oi on opc.main_order_no=oi.order_no WHERE opc.sub_type = #{subType} AND opc.`status` = 2
and opc.order_no in
#{item}
and opc.created_user = #{userName}
GROUP BY opc.order_no

mapper.select:
SELECT distinct opc.`id`, opc.main_order_no, opc.`customer_code` customerCode , (select si.supplier_ch_name from supplier_info si where si.supplier_code = opc.customer_code limit 1)customerName, opc.`cost_type_id` costTypeId, (SELECT ct.code_name FROM cost_type ct WHERE ct.id = opc.`cost_type_id` LIMIT 1) costType, opc.`cost_genre_id` costGenreId, (SELECT cg.name FROM cost_genre cg WHERE cg.id = opc.cost_genre_id LIMIT 1) costGenre, opc.`unit`, opc.`cost_code` costCode, (SELECT ci.name FROM cost_info ci WHERE ci.id_code = opc.`cost_code` LIMIT 1) costName, opc.`unit_price` unitPrice, opc.`number`, opc.`currency_code` currencyCode, (SELECT ci.currency_name FROM currency_info ci WHERE ci.currency_code = opc.`currency_code` LIMIT 1) currencyName, opc.`amount`, opc.`change_amount` changeAmount, opc.`exchange_rate` exchangeRate, opc.`remarks`, opc.`status`, opc.unloading_address unloadingAddress FROM order_payment_cost opc where opc.`main_order_no` = #{form.mainOrderNo}
and opc.sub_type = #{form.subType} and opc.order_no = #{form.subOrderNo} and opc.`status` in (0)
and opc.supplier_id = #{form.supplierId}

select date_format(oi.create_time,#{form.sqlFormat}) createTime,sum(ifnull(opc.change_amount,0)) changeAmount from order_info oi left join order_payment_cost opc on opc.main_order_no=oi.order_no
and opc.is_sum_to_main = true
and date_format(oi.create_time,#{form.sqlFormat}) between #{form.timeInterval[0]} and #{form.timeInterval[1]}
and opc.status in
#{item}
and oi.`legal_entity_id` in
#{legalId}
group by date_format(oi.create_time,#{form.sqlFormat}) order by date_format(oi.create_time,#{form.sqlFormat})

select opc.main_order_no,opc.order_no,opc.change_amount,opc.status,opc.is_internal,opc.is_sum_to_main, date_format(oi.create_time,#{form.sqlFormat}) orderCreatedTime from order_info oi left join order_payment_cost opc on opc.main_order_no=oi.order_no
and date_format(oi.create_time,#{form.sqlFormat}) between #{form.timeInterval[0]} and #{form.timeInterval[1]}
and opc.status in
#{item}
and oi.`legal_entity_id` in
#{legalId}
order by date_format(oi.create_time,#{form.sqlFormat})

