mapper.select:
SELECT distinct orc.`id`, orc.main_order_no, orc.`customer_code` customerCode, ci.`name` customerName , orc.`cost_type_id` costTypeId, (SELECT ct.code_name FROM cost_type ct WHERE ct.id = orc.`cost_type_id` LIMIT 1) costType, orc.`cost_genre_id` costGenreId, (SELECT cg.name FROM cost_genre cg WHERE cg.id = orc.cost_genre_id LIMIT 1) costGenre, orc.`unit`, orc.`cost_code` costCode, (SELECT ci.name FROM cost_info ci WHERE ci.id_code = orc.`cost_code` LIMIT 1) costName, orc.`unit_price` unitPrice, orc.`number`, orc.`currency_code` currencyCode, (SELECT ci.currency_name FROM currency_info ci WHERE ci.currency_code = orc.`currency_code` LIMIT 1) currencyName, orc.`amount`, orc.`change_amount` changeAmount, orc.`exchange_rate` exchangeRate, orc.`remarks`, orc.`status`, orc.unloading_address unloadingAddress FROM order_receivable_cost orc left join customer_info ci on orc.customer_code = ci.id_code where orc.`main_order_no` = #{form.mainOrderNo}
and (orc.sub_type = 'main' or (orc.is_sum_to_main = 1 and orc.`status` = '3'))
and ((orc.`status` in ('2','3') and orc.sub_type = 'main') or (orc.`status` in ('3') and orc.sub_type !='main' and orc.is_sum_to_main=true))
and ((orc.`status` in ('3') and orc.sub_type = 'main' and orc.is_bill='0') or (orc.`status` in ('3') and orc.sub_type !='main' and orc.is_sum_to_main=true and orc.is_bill='0'))
and orc.sub_type = #{form.subType} and orc.order_no = #{form.subOrderNo}
and orc.`status` in ('2','3') and orc.sub_type = #{form.subType} and orc.order_no = #{form.subOrderNo}
and orc.`status` ='3' and orc.sub_type = #{form.subType} and orc.order_no = #{form.subOrderNo} and orc.is_bill='0'

choose.when:
SELECT orc.main_order_no mainOrderNo, count( 1 ) FROM `order_receivable_cost` orc left join order_info oi on orc.main_order_no=oi.order_no WHERE orc.sub_type = "main" AND orc.`status` = 2 AND oi.`status` IN (1,3,4)
and orc.main_order_no in
#{item}
and oi.`legal_entity_id` in
#{legalId}
GROUP BY orc.main_order_no
