mapper.select:
SELECT distinct orc.`id`, orc.main_order_no, orc.`customer_code` customerCode, ci.`name` customerName , orc.`cost_type_id` costTypeId, (SELECT ct.code_name FROM cost_type ct WHERE ct.id = orc.`cost_type_id` LIMIT 1) costType, orc.`cost_genre_id` costGenreId, (SELECT cg.name FROM cost_genre cg WHERE cg.id = orc.cost_genre_id LIMIT 1) costGenre, orc.`unit`, orc.`cost_code` costCode, (SELECT ci.name FROM cost_info ci WHERE ci.id_code = orc.`cost_code` LIMIT 1) costName, orc.`unit_price` unitPrice, orc.`number`, orc.`currency_code` currencyCode, (SELECT ci.currency_name FROM currency_info ci WHERE ci.currency_code = orc.`currency_code` LIMIT 1) currencyName, orc.`amount`, orc.`change_amount` changeAmount, orc.`exchange_rate` exchangeRate, orc.`remarks`, orc.`status`, orc.unloading_address unloadingAddress, orc.department_id,orc.is_internal FROM order_receivable_cost orc left join customer_info ci on orc.customer_code = ci.id_code where orc.`main_order_no` = #{form.mainOrderNo}
and (orc.sub_type = 'main' or (orc.is_sum_to_main = 1 and orc.`status` = '3'))
and ((orc.`status` in ('2','3') and orc.sub_type = 'main') or (orc.`status` in ('3') and orc.sub_type !='main' and orc.is_sum_to_main=true))
and ((orc.`status` in ('3') and orc.sub_type = 'main' and orc.is_bill='0') or (orc.`status` in ('3') and orc.sub_type !='main' and orc.is_sum_to_main=true and orc.is_bill='0'))
and orc.sub_type = #{form.subType} and orc.order_no = #{form.subOrderNo}
and orc.`status` in ('2','3') and orc.sub_type = #{form.subType} and orc.order_no = #{form.subOrderNo}
and orc.`status` ='3' and orc.sub_type = #{form.subType} and orc.order_no = #{form.subOrderNo} and orc.is_bill='0'

choose.when:
SELECT orc.main_order_no mainOrderNo, count( 1 ) FROM `order_receivable_cost` orc left join order_info oi on orc.main_order_no=oi.order_no WHERE orc.sub_type = "main" AND orc.`status` = 2 AND oi.`status` IN (1,3,4)
and orc.main_order_no in
#{item}
and oi.`legal_entity_id` in
#{legalId}
GROUP BY orc.main_order_no

mapper.select:
select
main_order_no mainOrderNo,order_no orderNo,sum(change_amount) changeAmount
from order_receivable_cost


        and status in

            #{item}




            and is_sum_to_main = true

                and main_order_no in

                    #{orderNo}


            group by main_order_no


            and sub_type=#{subType}

                and order_no in

                    #{orderNo}


            group by main_order_no,tmp.order_no

select date_format(oi.create_time,#{form.sqlFormat}) createTime, sum(ifnull(orc.change_amount,0)) changeAmount from order_info oi left join order_receivable_cost orc on orc.main_order_no=oi.order_no
and orc.is_sum_to_main = true
and date_format(oi.create_time,#{form.sqlFormat}) between #{form.timeInterval[0]} and #{form.timeInterval[1]}
and orc.status in
#{item}
and oi.`legal_entity_id` in
#{legalId}
group by date_format(oi.create_time,#{form.sqlFormat}) order by date_format(oi.create_time,#{form.sqlFormat})

select orc.main_order_no,orc.order_no,orc.change_amount,orc.status,orc.is_internal,orc.is_sum_to_main, date_format(oi.create_time,#{form.sqlFormat}) orderCreatedTime from order_info oi left join order_receivable_cost orc on orc.main_order_no=oi.order_no
and date_format(oi.create_time,#{form.sqlFormat}) between #{form.timeInterval[0]} and #{form.timeInterval[1]}
and orc.status in
#{item}
and oi.`legal_entity_id` in
#{legalId}
order by date_format(oi.create_time,#{form.sqlFormat})

SELECT tmp.*, sum(IFNULL( cav.local_money, 0 )) heiXiaoAmount, sum(IFNULL( cav.short_amount, 0 )) shortAmount FROM ( SELECT date_format(oi.create_time,#{form.sqlFormat}) createTime, orc.customer_name, orc.customer_code, orc.legal_id, orc.legal_name, sum( obct.local_money ) localMoney, obct.bill_no FROM order_info oi INNER JOIN order_receivable_cost orc ON orc.main_order_no = oi.order_no INNER JOIN order_bill_cost_total obct ON orc.id = obct.cost_id AND obct.money_type = 2
orc.is_bill = 2 AND orc.is_sum_to_main = TRUE
and date_format(oi.create_time,#{form.sqlFormat}) between #{form.timeInterval[0]} and #{form.timeInterval[1]}
and orc.status in
#{item}
and oi.`legal_entity_id` in
#{legalId}
GROUP BY obct.bill_no ORDER BY oi.create_time ) tmp LEFT JOIN cancel_after_verification cav ON cav.bill_no = tmp.bill_no GROUP BY tmp.bill_no
