mapper.select:
select s.id, s.supplier_code supplierCode, s.product_classify_ids productClassify, s.supplier_ch_name supplierChName, s.supplier_en_name supplierEnName, s.state_code stateCode, s.contacts, s.company_name companyName, s.contact_number contactNumber, s.contact_phone contactPhone, s.address, s.pid, s.pname, s.cid, s.cname, s.zip_code zipCode, s.ext_setting_id extSettingId, settlement_type settlementType, payment_day paymentDay, tax_receipt tax_receipt, rate, s.buyer_id buyerId,s.status, s.create_user createUser, s.create_time createTime, s.update_time updateTime,s.update_user updateUser, a.audit_status auditStatus, a.audit_comment auditComment from supplier_info s left join audit_info a on s.id = a.ext_id and ext_desc=#{form.auditTableDesc}
and s.supplier_code like concat ('%',#{form.supplierCode},'%')
and s.supplier_ch_name like concat ('%',#{form.supplierChName},'%')
and a.audit_status = #{form.auditStatus}

select s.id, s.supplier_code supplierCode, s.product_classify_ids productClassify, s.supplier_ch_name supplierChName, s.supplier_en_name supplierEnName, s.state_code stateCode, s.contacts, s.company_name companyName, s.contact_number contactNumber, s.contact_phone contactPhone, s.address, s.pid, s.pname, s.cid, s.cname, s.zip_code zipCode, s.ext_setting_id extSettingId, settlement_type settlementType, payment_day paymentDay, tax_receipt tax_receipt, rate, s.buyer_id buyerId,s.status, s.create_user createUser, s.create_time createTime, s.update_time updateTime,s.update_user updateUser, from supplier_info s
and s.status=#{supplierStatus} and s.id in (select a.ext_id from audit_info a where a.ext_desc=#{tablesDesc} and a.audit_status=#{auditStatus}
and a.ext_id in
#{item}
group by ext_id, ext_desc order by id desc )
