mapper.select:
select sfo.id id,sfo.order_no orderNo,main_order_no mainOrderNo,status, process_status processStatus, legal_entity_id legalEntityId,legal_name leaglName, unit_code unitCode,department_id departmentId,waybill_no waybillNo, in_plate_number inPlateNumber, in_warehouse_number inWarehouseNumber, create_user createUser, create_time createTime, remarks, receiving_orders_date receivingOrdersDate, estimated_arrival_time estimatedArrivalTime, expected_delivery_time expectedDeliveryTime, out_plate_number outPlateNumber, out_warehouse_number outWarehouseNumber, is_warehouse isWarehouse -- (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.order_no = sio.`order_no`) + -- (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.order_no = sio.`order_no`)>0 THEN TRUE -- ELSE FALSE -- END) cost from storage_fast_order sfo
and sfo.order_no like concat('%',#{form.orderNo},'%')
and sfo.main_order_no like concat('%',#{form.mainOrderNo},'%')
and sfo.in_warehouse_number like concat('%',#{form.inWarehouseNumber},'%')
and sfo.legal_name like concat('%',#{form.legalName},'%')
and sfo.out_warehouse_number like concat('%',#{form.outWarehouseNumber},'%')
and sfo.process_status like concat('%',#{form.processStatus},'%')
and sfo.create_user like concat('%',#{form.createUser},'%')
and sfo.main_order_no IN
#{item}
and sfo.status = #{form.status}
and sfo.process_status IN
#{item}
and sfo.create_time >= #{form.startTime}
and sfo.create_time <= #{form.endTime}
-- AND (EXISTS (SELECT 1 FROM order_payment_cost opc WHERE opc.`order_no` = sio.order_no AND opc.`status` = -- '2' and opc.sub_type = 'cci') -- OR EXISTS (SELECT 1 FROM order_receivable_cost orc WHERE orc.`order_no` = sio.order_no AND orc.`status` = -- '2' and orc.sub_type = 'cci')) and sfo.`order_no` in
#{orderNo}
or sfo.`order_no` in
#{orderNo}
and sfo.`legal_entity_id` in
#{legalId}
order by sfo.id desc
