mapper.select:
select sio.id id,sio.order_no orderNo,main_order_no mainOrderNo,status, process_status processStatus, legal_entity_id legalEntityId,legal_name leaglName, unit_code unitCode,unit_code_name unitCodeName,department_id departmentId,waybill_no waybillNo, plate_number plateNumber,warehouse_number warehouseNumber, create_user createUser, create_time createTime, remarks, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.order_no = sio.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.order_no = sio.`order_no`)>0 THEN TRUE ELSE FALSE END) cost from storage_input_order sio
and sio.order_no like concat('%',#{form.orderNo},'%')
and sio.warehouseNumber like concat('%',#{form.warehouseNumber},'%')
and sio.process_status like concat('%',#{form.processStatus},'%')
and sio.create_user like concat('%',#{form.createUser},'%')
and sio.main_order_no IN
#{item}
and sio.status = #{form.status}
and sio.process_status IN
#{item}
and sio.create_time >= #{form.startTime}
and sio.create_time <= #{form.endTime}
AND (EXISTS (SELECT 1 FROM order_payment_cost opc WHERE opc.`order_no` = sio.order_no AND opc.`status` = '2' and opc.sub_type = 'cc') OR EXISTS (SELECT 1 FROM order_receivable_cost orc WHERE orc.`order_no` = sio.order_no AND orc.`status` = '2' and orc.sub_type = 'cc'))
and sio.`legal_entity_id` in
#{legalId}
order by sio.id desc

select igor.wbn warehousingBatchNo,sio.id id,sio.order_no orderNo,main_order_no mainOrderNo,status, process_status processStatus, legal_entity_id legalEntityId,legal_name leaglName, unit_code unitCode,unit_code_name unitCodeName,department_id departmentId,waybill_no waybillNo, plate_number plateNumber,warehouse_number warehouseNumber, create_user createUser, create_time createTime, remarks, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.order_no = sio.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.order_no = sio.`order_no`)>0 THEN TRUE ELSE FALSE END) cost from storage_input_order sio right join (select warehousing_batch_no wbn,order_id,order_no from in_goods_operation_record group by wbn) igor on igor.order_id = sio.id
and sio.order_no like concat('%',#{form.orderNo},'%')
and sio.warehouseNumber like concat('%',#{form.warehouseNumber},'%')
and sio.process_status like concat('%',#{form.processStatus},'%')
and sio.create_user like concat('%',#{form.createUser},'%')
and sio.main_order_no IN
#{item}
and sio.status = #{form.status}
and sio.process_status IN
#{item}
and sio.create_time >= #{form.startTime}
and sio.create_time <= #{form.endTime}
AND (EXISTS (SELECT 1 FROM order_payment_cost opc WHERE opc.`order_no` = sio.order_no AND opc.`status` = '2' and opc.sub_type = 'cc') OR EXISTS (SELECT 1 FROM order_receivable_cost orc WHERE orc.`order_no` = sio.order_no AND orc.`status` = '2' and orc.sub_type = 'cc'))
and sio.`legal_entity_id` in
#{legalId}
order by sio.id desc
