mapper.select:
select soo.id orderId,soo.order_no orderNo,main_order_no mainOrderNo,status, process_status processStatus, legal_entity_id legalEntityId,legal_name leaglName, unit_code unitCode,unit_code_name unitCodeName,department_id departmentId, plate_information plateInformation, create_user createUser, create_time createTime, remarks, (CASE WHEN (SELECT COUNT(1) FROM order_payment_cost opc WHERE opc.order_no = soo.`order_no`) + (SELECT COUNT(1) FROM order_receivable_cost orc WHERE orc.order_no = soo.`order_no`)>0 THEN TRUE ELSE FALSE END) cost from storage_out_order soo
and soo.order_no like concat('%',#{form.orderNo},'%')
and soo.process_status like concat('%',#{form.processStatus},'%')
and soo.create_user like concat('%',#{form.createUser},'%')
and soo.main_order_no IN
#{item}
and soo.status = #{form.status}
and soo.process_status IN
#{item}
and soo.create_time >= #{form.startTime}
and soo.create_time <= #{form.endTime}
AND (EXISTS (SELECT 1 FROM order_payment_cost opc WHERE opc.`order_no` = soo.order_no AND opc.`status` = '2' and opc.sub_type = 'cc') OR EXISTS (SELECT 1 FROM order_receivable_cost orc WHERE orc.`order_no` = soo.order_no AND orc.`status` = '2' and orc.sub_type = 'cc'))
and soo.`legal_entity_id` in
#{legalId}
order by soo.id desc
