mapper.select:
select id, area_name areaName, area_code areaCode, warehouse_id warehouseId, status, create_user createUser, create_time createTime, remarks
from warehouse_area wa
left join warehouse w on wa.warehouseId = w.id
where status = 1

    and wa.areaName = #{form.areaName}


    and wa.areaCode = #{form.areaCode}


    and w.name = #{form.name}


    and w.code = #{form.code}


    and w.create_time  >=  concat(date_format(#{form.startTime}, '%Y-%m-%d'), ' 00:00:00')
    and w.create_time  <=  concat(date_format(#{form.endTime}, '%Y-%m-%d'), ' 23:59:59');

order by createTime desc

select id, area_name areaName, area_code areaCode, warehouse_id warehouseId, status, create_user createUser, create_time createTime, remarks,
w.name name ,w.code code
from warehouse_area wa
left join warehouse w on wa.warehouseId = w.id
where status = 1

    and wa.areaName = #{form.areaName}


    and wa.areaCode = #{form.areaCode}


    and w.name = #{form.name}


    and w.code = #{form.code}


    and w.create_time >=  #{form.startTime}


    and w.create_time <=  #{form.endTime}

order by createTime desc
