mapper.sql:
SELECT

FROM


    is_deleted = 0

        AND warehouse_id = ${receipt.warehouseId}



        AND warehouse LIKE CONCAT('%',#{receipt.warehouse}, '%')



        AND ower_id = ${receipt.owerId}



        AND ower LIKE CONCAT('%',#{receipt.ower}, '%')



        AND receipt_notice_num LIKE CONCAT('%',#{receipt.receiptNoticeNum}, '%')



        AND receipt_num LIKE CONCAT('%',#{receipt.receiptNum}, '%')



        AND supplier_id = ${receipt.supplierId}



        AND supplier LIKE CONCAT('%',#{receipt.supplier}, '%')



        AND order_source_code = ${receipt.orderSourceCode}



        AND order_source LIKE CONCAT('%',#{receipt.orderSource}, '%')



        AND planned_receiving_time = ${receipt.plannedReceivingTime}



        AND total_num = ${receipt.totalNum}



        AND total_weight = ${receipt.totalWeight}



        AND total_volume = ${receipt.totalVolume}



        AND actual_num = ${receipt.actualNum}



        AND actual_weight = ${receipt.actualWeight}



        AND actual_volume = ${receipt.actualVolume}



        AND receiver LIKE CONCAT('%',#{receipt.receiver}, '%')



        AND status = ${receipt.status}



        AND receiving_time = ${receipt.receivingTime}



        AND column_one LIKE CONCAT('%',#{receipt.columnOne}, '%')



        AND column_two LIKE CONCAT('%',#{receipt.columnTwo}, '%')



        AND column_three LIKE CONCAT('%',#{receipt.columnThree}, '%')



        AND is_put _shelf = ${receipt.isPut Shelf}



        AND tenant_code LIKE CONCAT('%',#{receipt.tenantCode}, '%')



        AND remark LIKE CONCAT('%',#{receipt.remark}, '%')



        AND process_flag LIKE CONCAT('%',#{receipt.processFlag}, '%')

mapper.select:
SELECT r.id, r.warehouse_id, r.warehouse, r.ower_id, r.ower, r.receipt_notice_num, r.receipt_num, r.supplier_id, r.supplier, r.order_source_code, r.order_source, r.planned_receiving_time, r.total_num, r.total_weight, r.total_volume, r.actual_num, r.actual_weight, r.actual_volume, r.receiver, r.STATUS, r.receiving_time, r.column_one, r.column_two, r.column_three, r.is_put_shelf, r.tenant_code, r.remark, r.is_deleted, r.create_by, r.create_time, r.update_by, r.update_time, r.process_flag, r.qc_no, r.main_order_number, r.order_type, r.unit, r.car_barnd, r.car_model, r.client_code, group_concat( wm.in_warehouse_number ) inWarehouseNumberList FROM wms_receipt r LEFT JOIN wms_material wm on r.id=wm.order_id
r.is_deleted=0 and wm.is_deleted=0
AND r.id = #{receipt.id}
AND r.warehouse_id = #{receipt.warehouseId}
AND r.status = #{receipt.status}
AND r.supplier_id = #{receipt.supplierId}
AND r.order_type = #{receipt.orderType}
AND r.car_barnd = #{receipt.carBarnd}
AND r.main_order_number = #{receipt.mainOrderNumber}
AND r.receipt_num LIKE CONCAT('%',#{receipt.receiptNum}, '%')
AND r.ower LIKE CONCAT('%',#{receipt.ower}, '%')
and r.receiving_time between #{receipt.timeOfReceipt[0]} and #{receipt.timeOfReceipt[1]}
group by r.receipt_num ORDER BY r.create_time DESC

SELECT r.id, r.warehouse_id, r.warehouse, r.ower_id, r.ower, r.receipt_notice_num, r.receipt_num, r.supplier_id, r.supplier, r.order_source_code, r.order_source, r.planned_receiving_time, r.total_num, r.total_weight, r.total_volume, r.actual_num, r.actual_weight, r.actual_volume, r.receiver, r.STATUS, (SELECT sdi.item_text FROM sys_dict_item sdi LEFT JOIN sys_dict sd ON sd.is_deleted = 0 AND sd.id = sdi.dict_id WHERE sdi.is_deleted = 0 AND sdi.`status` = 1 AND sd.dict_code = 'receivingOrderStatus' AND r.STATUS = sdi.item_value) AS statusDetails, r.receiving_time, r.column_one, r.column_two, r.column_three, r.is_put_shelf, r.tenant_code, r.remark, r.is_deleted, r.create_by, r.create_time, r.update_by, r.update_time, r.process_flag, r.qc_no FROM wms_receipt r
r.is_deleted=0 AND r.status !=4 AND r.status !=3
AND r.id = #{receipt.id}
AND r.warehouse_id = #{receipt.warehouseId}
AND r.status = #{receipt.status}
AND r.receipt_num LIKE CONCAT('%',#{receipt.receiptNum}, '%')
AND r.ower LIKE CONCAT('%',#{receipt.ower}, '%')
and r.receiving_time between #{receipt.timeOfReceipt[0]} and #{receipt.timeOfReceipt[1]}
and r.ower_id in
#{id}
and r.warehouse_id in
#{id}
ORDER BY r.create_time DESC

SELECT r.receipt_num, r.warehouse, r.receipt_notice_num, r.ower, (SELECT sdi.item_text FROM sys_dict_item sdi LEFT JOIN sys_dict sd ON sd.is_deleted = 0 AND sd.id = sdi.dict_id WHERE sdi.is_deleted = 0 AND sdi.`status` = 1 AND sd.dict_code = 'receivingOrderStatus' AND r.STATUS = sdi.item_value) AS statusDetails, r.actual_num, r.actual_weight, r.actual_volume, r.receiver, r.receiving_time, r.planned_receiving_time, r.qc_no, r.create_by, r.create_time FROM wms_receipt r
r.is_deleted=0
AND r.id = #{receipt.id}
AND r.warehouse_id = #{receipt.warehouseId}
AND r.status = #{receipt.status}
AND r.receipt_num LIKE CONCAT('%',#{receipt.receiptNum}, '%')
AND r.ower LIKE CONCAT('%',#{receipt.ower}, '%')
and r.planned_receiving_time between #{receipt.arrivalTime[0]} and #{receipt.arrivalTime[1]}
and r.receiving_time between #{receipt.timeOfReceipt[0]} and #{receipt.timeOfReceipt[1]}
and r.ower_id in
#{id}
and r.warehouse_id in
#{id}
ORDER BY r.create_time DESC
