mapper.select:
SELECT id, customer_code, customer_name, customer_type_id, user_name, phone_number, email, address, status, remark, is_deleted, create_by, create_time, update_by, update_time FROM wms_customer_info WHERE is_deleted = 0
and customer_type_id =#{wmsCustomerInfo.customerTypeId,jdbcType=BIGINT}
ORDER BY id desc

SELECT id, customer_code, customer_name, customer_type_id, user_name, phone_number, email, address, status, remark, is_deleted, create_by, create_time, update_by, update_time FROM wms_customer_info WHERE is_deleted = 0
and customer_code =#{customerCode,jdbcType=VARCHAR}
and customer_name =#{customerName,jdbcType=VARCHAR}
